INSERT INTO `detail_news` VALUES (1, 'ว่าด้วยระเบียบของปีงบประมาณ', '', 'จะให้อภัยได้ไหมให้ใครคนนึง ที่ทำให้เธอเหนื่อยหัวใจ \r\nใครสักคนที่เธอรัก และคิดร่วมวันฝันใฝ่ แต่เหมือนใจร้ายกว่าใครทุกคนเธอให้อภัยได้ไหมให้คนๆนึง หนึ่งคนที่กำลังทุกทนต้องทำให้เธอช้ำ ทำให้เธอหมองหม่น ทั้งที่รักเธอจนแทบจะขาดใจ เข้าใจเสมอ ถ้าเธอโกรธฉัน ที่ทำกับเธอวันนั้นฉันเองก็ยังเสียใจเข้าใจเสมอ ถ้าเธอไม่คิดให้อภัย ฉันก็คงโชคร้ายกว่าใครทุได้โปรดสบตากับฉันเหมือนวันเดิมๆ ได้โปรดอย่าเพิ่งเมินหมางไปอยากให้เธอเข้าใจฉัน และพร้อมร่วมวันฝันใฝ่ บอกฉันได้ไหมว่าเธอก็ยังเหมือนเดิม\r\n', 'UserSelections.txt', '23ELONgFri30929.txt', '2006-10-06', '', '', '15:09:29', 'mljeed', 1001, '2', '10002', '0', '', 'y');
INSERT INTO `detail_news` VALUES (3, 'ประกาศการรับสมัครงาน', '', 'โดยทางกระทรวงเปิดรับ 1 ตำแหน่ง คือ เจ้าหน้าที่ประชาสัมพันธ์', 'วิชา     ดนตรี.doc', 'XoVoNqzSat62227.doc', '2006-10-14', '', '', '18:22:27', 'admin', 1004, '2', '10002', '0', '', 'y');
INSERT INTO `detail_news` VALUES (4, 'ข่าวสมัครงาน', '', 'ประกาศรับสมัครพนังงาน', 'วิชา     ดนตรี.doc', 'vSbGjYnSat62710.doc', '2006-10-14', '', '', '18:27:10', 'admin', 1004, '2', '10002', '0', '', 'y');
INSERT INTO `detail_news` VALUES (5, 'ภาพการเปิดงานแสดงนวัตกรรมใหม่', 'ในวันที่ 11 ตุลาคมมีการเปิดพิธีของกรมสาธารณสุขเกี่ยวกับการเลิกบุหรี่ของหน่วยงานในกระทรวงวิทยาศาสตร์', 'ในวันที่ 11 ตุลาคมมีการเปิดพิธีของกรมสาธารณสุขเกี่ยวกับการเลิกบุหรี่ของหน่วยงานในกระทรวงวิทยาศาสตร์ หน่วยข่าวความมั่นคงจากกองอำนวยการเสริมสร้างสันติสุขจังหวัดชายแดนภาคใต้ (กอ.สสส.จชต.) ได้แจ้งเตือนให้เจ้าหน้าที่ในจังหวัดชายแดนภาคใต้ ระวังป้องกันการโจมตีและก่อความวุ่นวายของแนวร่วมกลุ่มอาร์เคเค และกลุ่มเปอร์มูดอครั้งใหญ่ ในระหว่างวันที่ 16-20 ต.ค.นี้ โดยกลุ่มนี้เป็นกลุ่มก่อการร้ายที่ไม่เห็นด้วยกับการเจรจาเพื่อยุติการก่อการร้ายระหว่างตัวแทนของกองทัพบกกับตัวแทนกลุ่มพูโลเก่า พูโลใหม่ และมูจาฮีดินอิสลามปัตตานี ที่มีอดีตนายกรัฐมนตรีมาเลเชียเป็นประธาน', '', 'JMnMPsjPicture 028.jpg', '2006-10-14', '', '', '20:12:06', 'admin', 1003, '1', '10002', '0', '', 'y');
INSERT INTO `detail_news` VALUES (6, 'ระเบียบวาระปีงบประมาณ', 'แต่เมื่อใช้อาร์เรย์ไป ขนาดของมันจะปรับเปลี่ยนได้ คือขยายจำนวนข้อมูลที่เก็บอยู่ภายในอาร์เรย์ ตามจำนวนข้อมูลที่เราใส่เพิ่มเข้าไป จากตัวอย่างข้างบน ในกรณีที่เรามิได้กำหนดเลขดัชนี (index) ก็หมายความว่า จะมีการขยายขนาดของอาร์เรย์เพิ่มขึ้นอีกหนึ่งโดยอัตโนมัติ ทุกครั้งที่เราใส่ข้อมูลที่อยู่ทางขวา และค่าที่เรากำหนดจากทางขวามือ และจะเก็บไว้ในที่ใหม่ของอาร์เรย์ เราไม่ต้องคำนึงถึงเรื่องการจอง หรือ ปลดปล่อยหน่วยความจำของอาร์เรย์ เหมือนอย่างในกรณีของอาร์เรย์ แบบไดนามิกในภาษาซี ', 'แต่เมื่อใช้อาร์เรย์ไป ขนาดของมันจะปรับเปลี่ยนได้ คือขยายจำนวนข้อมูลที่เก็บอยู่ภายในอาร์เรย์ ตามจำนวนข้อมูลที่เราใส่เพิ่มเข้าไป จากตัวอย่างข้างบน ในกรณีที่เรามิได้กำหนดเลขดัชนี (index) ก็หมายความว่า จะมีการขยายขนาดของอาร์เรย์เพิ่มขึ้นอีกหนึ่งโดยอัตโนมัติ ทุกครั้งที่เราใส่ข้อมูลที่อยู่ทางขวา และค่าที่เรากำหนดจากทางขวามือ และจะเก็บไว้ในที่ใหม่ของอาร์เรย์ เราไม่ต้องคำนึงถึงเรื่องการจอง หรือ ปลดปล่อยหน่วยความจำของอาร์เรย์ เหมือนอย่างในกรณีของอาร์เรย์ แบบไดนามิกในภาษาซี ', 'ไม่มีข้อมูล', 'hMler0Zbritish_columbia.jpg', '2006-10-27', '', '', '13:44:34', 'admin', 1001, '2', '10002', '0', '', 'y');
INSERT INTO `detail_news` VALUES (7, '"เสี่ยงต่อการเกิดโรคหัวใจล้มเหลว"', '<TABLE cellSpacing=0 cellPadding=3 width="100%" align=center border=0>\r\n<TBODY>\r\n<TR bgColor=#f1e9df>\r\n<TD vAlign=top align=middle>\r\n<TABLE cellSpacing=2 cellPadding=2 width="95%" border=0>\r\n<TBODY>\r\n<TR>\r\n<TD vAlign=top><FONT color=#920000><B>"เสี่ยงต่อการเกิดโรคหัวใจล้มเหลว"<BR></B></FONT><BR><FONT color=red><B>คนที่นอนกรนรีบหันมาอ่านตรงนี้เลย เพราะมีผลวิจัยจากประเทศออสเตรเลียออกมาว่า</B></FONT> การนอนกรนนั้น ค่อนข้างเสี่ยงต่อการเกิดโรคหัวใจล้มเหลวได้ <BR><BR>โดยแพทย์ของศูนย์การนอนหลับผิดปกติ โรงพยาบาลอัลเฟรด ในเมลเบิร์น พบความเชื่อมโยงระหว่างการนอนกรน หรือการหยุดหายใจเป็นห้วงเล็กๆ ระหว่างการนอน ด้วยการให้คนไข้ 40 ราย <BR></TD></TR></TBODY></TABLE></TD></TR>\r\n<TR bgColor=#f1e9df>\r\n<TD vAlign=top align=middle>\r\n<TABLE cellSpacing=2 cellPadding=2 width="95%" border=0>\r\n<TBODY>\r\n<TR>\r\n<TD vAlign=top><FONT color=#920000><B>"นอนกรนเกิน 3 ครั้งต่อสัปดาห์"<BR></B></FONT><BR>นอนสวมหน้ากากปั๊มอากาศเข้าจมูกเพื่อป้องกันการนอนกรน และลดโอกาสเสี่ยงของการหัวใจล้มเหลว <BR><BR>รองศาสตราจารย์แมธธิว นอร์ตัน กล่าวว่า ปัจจุบันจะเห็นได้ว่า คนที่นอนกรนโดยทั่วไปจะเป็นโรคหัวใจ และสิ่งที่เราแสดงให้เห็นก็คือ หากรักษาอาการกรนได้ เรื่องโรคหัวใจก็จะดีขึ้น การค้นพบนี้น่าจะมีส่วนช่วยให้คนเปลี่ยนมุมมองต่อการนอนกรน ว่าเป็นสิ่งที่น่ารำคาญ แต่ควรจะระวังหากว่านอนกรนเกิน 3 ครั้งต่อสัปดาห์ น่าจะไปพบแพทย์.<BR></TD></TR></TBODY></TABLE></TD></TR></TBODY></TABLE>', '<TABLE cellSpacing=0 cellPadding=3 width="100%" align=center border=0>\r\n<TBODY>\r\n<TR bgColor=#f1e9df>\r\n<TD vAlign=top align=middle>\r\n<TABLE cellSpacing=2 cellPadding=2 width="95%" border=0>\r\n<TBODY>\r\n<TR>\r\n<TD vAlign=top><FONT color=#920000><B>"เสี่ยงต่อการเกิดโรคหัวใจล้มเหลว"<BR></B></FONT><BR><FONT color=red><B>คนที่นอนกรนรีบหันมาอ่านตรงนี้เลย เพราะมีผลวิจัยจากประเทศออสเตรเลียออกมาว่า</B></FONT> การนอนกรนนั้น ค่อนข้างเสี่ยงต่อการเกิดโรคหัวใจล้มเหลวได้ <BR><BR>โดยแพทย์ของศูนย์การนอนหลับผิดปกติ โรงพยาบาลอัลเฟรด ในเมลเบิร์น พบความเชื่อมโยงระหว่างการนอนกรน หรือการหยุดหายใจเป็นห้วงเล็กๆ ระหว่างการนอน ด้วยการให้คนไข้ 40 ราย <BR></TD></TR></TBODY></TABLE></TD></TR>\r\n<TR bgColor=#f1e9df>\r\n<TD vAlign=top align=middle>\r\n<TABLE cellSpacing=2 cellPadding=2 width="95%" border=0>\r\n<TBODY>\r\n<TR>\r\n<TD vAlign=top><FONT color=#920000><B>"นอนกรนเกิน 3 ครั้งต่อสัปดาห์"<BR></B></FONT><BR>นอนสวมหน้ากากปั๊มอากาศเข้าจมูกเพื่อป้องกันการนอนกรน และลดโอกาสเสี่ยงของการหัวใจล้มเหลว <BR><BR>รองศาสตราจารย์แมธธิว นอร์ตัน กล่าวว่า ปัจจุบันจะเห็นได้ว่า คนที่นอนกรนโดยทั่วไปจะเป็นโรคหัวใจ และสิ่งที่เราแสดงให้เห็นก็คือ หากรักษาอาการกรนได้ เรื่องโรคหัวใจก็จะดีขึ้น การค้นพบนี้น่าจะมีส่วนช่วยให้คนเปลี่ยนมุมมองต่อการนอนกรน ว่าเป็นสิ่งที่น่ารำคาญ แต่ควรจะระวังหากว่านอนกรนเกิน 3 ครั้งต่อสัปดาห์ น่าจะไปพบแพทย์.<BR></TD></TR></TBODY></TABLE></TD></TR></TBODY></TABLE>', 'ไม่มีข้อมูลลง', 'tAjA9E5000000075497.jpg', '2006-10-31', '', '', '11:39:58', 'admin', 1001, '2', '10002', '0', '', 'y');
INSERT INTO `detail_news` VALUES (8, 'รับเจ้าหน้าที่ประสานงานธุรการของกองการเจ้าหน้าที่', '<STRONG>ให้เกิดการเหนื่อยล้าของสายตาได้ ซึ่งวันนี้เราก็มีเคล็ดลับในการช่วยผ่อนคลายความเหนื่อยล้าทางสายตามาฝากกันค่ะ<BR></STRONG><BR><FONT color=#3366cc>1. หลับตาลง หลังจากนั้นใช้ฝ่ามือทั้งสองข้างปิดที่ดวงตา ความอบอุ่นจากฝ่ามือจะช่วยบรรเทาความเหนื่อยล้าดังกล่าวลงได้<BR></FONT><BR><FONT color=#3366cc>2. หลับตาลง ปล่อยแขนลงข้างลำตัว อาจทำในระหว่างที่ยังนั่งอยุ่ที่เก้าอี้หน้าคอมพ์ก้ได้นะคะ หลังจากนั้นก็สูดหายใจเข้าลึก ๆ หลาย ๆ ครั้ง<BR></FONT><BR><FONT color=#330033 size=4><STRONG><EM>3. หลังจากที่หลับตาแล้ว เมื่อลืมตาขึ้นมาก็ควรค่อย ๆ มองจอคอมพิวเตอร์อย่างกว้าง ๆ ทั่วทั้งจอก่อน หรือบริเวณที่เป็นพื้นที่สีขาว <BR><BR>4. ปิดตาลงอีกครั้ง หายใจเข้าลึก ๆ แล้วค่อย ๆ ลืมตา<BR><BR>ทำแบบนี้ซ้ำ ๆ กันไปมาอีกสัก 3 ครั้ง คุณก็จะรู้สึกผ่อนคลายขึ้นค่ะ...</EM></STRONG></FONT>', '<STRONG>ให้เกิดการเหนื่อยล้าของสายตาได้ ซึ่งวันนี้เราก็มีเคล็ดลับในการช่วยผ่อนคลายความเหนื่อยล้าทางสายตามาฝากกันค่ะ<BR></STRONG><BR><FONT color=#3366cc>1. หลับตาลง หลังจากนั้นใช้ฝ่ามือทั้งสองข้างปิดที่ดวงตา ความอบอุ่นจากฝ่ามือจะช่วยบรรเทาความเหนื่อยล้าดังกล่าวลงได้<BR></FONT><BR><FONT color=#3366cc>2. หลับตาลง ปล่อยแขนลงข้างลำตัว อาจทำในระหว่างที่ยังนั่งอยุ่ที่เก้าอี้หน้าคอมพ์ก้ได้นะคะ หลังจากนั้นก็สูดหายใจเข้าลึก ๆ หลาย ๆ ครั้ง<BR></FONT><BR><FONT color=#330033 size=4><STRONG><EM>3. หลังจากที่หลับตาแล้ว เมื่อลืมตาขึ้นมาก็ควรค่อย ๆ มองจอคอมพิวเตอร์อย่างกว้าง ๆ ทั่วทั้งจอก่อน หรือบริเวณที่เป็นพื้นที่สีขาว <BR><BR>4. ปิดตาลงอีกครั้ง หายใจเข้าลึก ๆ แล้วค่อย ๆ ลืมตา<BR><BR>ทำแบบนี้ซ้ำ ๆ กันไปมาอีกสัก 3 ครั้ง คุณก็จะรู้สึกผ่อนคลายขึ้นค่ะ...</EM></STRONG></FONT>', 'ไม่มีข้อมูลลง', '76FWfgx000000075500.jpg', '2006-10-31', '', '', '11:44:47', 'admin', 1004, '2', '10002', '0', '', 'y');
INSERT INTO `detail_news` VALUES (9, 'เริ่มหนาวแล้ว-นักเที่ยวแห่ภูทับเบิก', '<DIV align=center><STRONG>เริ่มหนาวแล้ว-นักเที่ยวแห่ภูทับเบิก</STRONG><BR><BR>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; เมื่อเวลา 07.00 น. วันที่ 1 พ.ย. นายจินดา กมลธรไท นายกสมาคมธุรกิจท่องเที่ยว จ.เพชรบูรณ์ กล่าวว่า ขณะนี้อากาศในช่วงปลายฝน ต้นหนาวของจ.เพชรบูรณ์ มีสภาพอากาศหนาวเย็นและจะเย็นขึ้นตามลำดับ <STRONG>โดยเฉพาะที่ภูทับเบิกบนเทือกเขาเพชรบูรณ์ สูงกว่าระดับน้ำทะเล 1,800 เมตร ในช่วงเช้าอุณหภูมิจะอยู่ที่ 10 องศาเซลเซียส มีคลื่นทะเลหมอกปกคลุมไปทั่วเป็นบริเวณกว้าง</STRONG> <BR><BR>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <FONT color=#ac002b>ชาวบ้านส่วนมากซึ่งเป็นชาวเขาเผ่าม้งเริ่มนอนผิงไฟกันแล้ว โดยทุกบ้านจะก่อกองไฟไว้ในบ้านเพื่อให้ร่างกายอบอุ่น บางบ้านจะใช้วิธีก่อกองไฟไว้ใต้แคร่สำหรับนอน ถ้าไม่ทำเช่นนี้ร่างกายจะทนกับสภาพอากาศที่หนาวเย็นไม่ได้ ส่วนของนักท่องเที่ยวเริ่มพากันทยอยเดินทางขึ้นไปสัมผัสอากาศหนาวเย็นกันบ้างแล้ว เชื่อว่าในช่วงวันหยุดจะมีมากขึ้น</FONT><BR><BR>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; ส่วนที่อ.เขาค้อ นายจีระศักดิ์ ศักรางกูร นายอำเภอเขาค้อ กล่าวว่า <STRONG>อากาศที่อ.เขาค้อ มีสภาพอากาศที่หนาวอุณหภูมิอยู่ที่ 12-15 องศาเซลเซียส ทุกวัน</STRONG>จะมีนักท่องเที่ยวเริ่มพากันทยอยขึ้นไปพักผ่อนตามรีสอร์ต บ้านพักกันบ้างแล้ว แต่จะมีมากในช่วงที่เป็นวันหยุด ส่วนรีสอร์ตต่างๆ ที่มีอยู่มากมายบางแห่งถูกจองไว้เต็มไปจนถึงเดือนมี.ค.2550<BR><BR>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <FONT color=#0000d7>&nbsp;แต่สำหรับที่กางเต็นท์ยังมีที่ว่างให้กางเต็นท์ได้ ที่ผ่านมามักจะถูกร้องเรียนจากนักท่องเที่ยวว่า อาหาร น้ำดื่ม และที่พักมีราคาแพง ซึ่งเรื่องนี้ตนได้ประชุมชี้แจงให้กับบรรดาผู้ประกอบการให้คำนึงถึงชื่อเสียงของเขาค้อ ถ้าหากนักท่องเที่ยวถูกเอาเปรียบมาก ก็จะพากันไปเที่ยวที่แห่งอื่น จะทำให้อยู่กันไม่ได้ ไม่ควรคิดเอากำไรมาก<BR></FONT><BR>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; ที่อ.น้ำหนาว นายขาว คำมาตร นายอำเภอน้ำหนาว กล่าวว่า สภาพอากาศที่หนาวเย็น อุณหภูมิอยู่ที่ 9-12 องศาเซลเซียส ชาวบ้านส่วนมากมีฐานะยากจน จะพากันออกมาก่อกองไฟผิงกัน บางครั้งก็ต้องพาลูกหลานนอนกันข้างกองไฟ <STRONG>ปกติสภาพอากาศที่น้ำหนาวจะมีอากาศที่เย็นตลอดทั้งปี คาดว่าปีนี้จะหนาวกว่าทุกปีที่ผ่านมา และจะหนาวนาน ชาวบ้านเริ่มประสบกับปัญหาขาดแคลนเสื้อผ้า ผ้าห่ม</STRONG> ซึ่งขณะนี้อำเภอกำลังเตรียมการช่วยเหลืออยู่ ซึ่งคงจะช่วยได้เพียงบางส่วน เพราะคนที่ขาดแคลนมีมาก และส่วนมากยากจน<BR></DIV>', '<DIV align=center><STRONG>เริ่มหนาวแล้ว-นักเที่ยวแห่ภูทับเบิก</STRONG><BR><BR>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; เมื่อเวลา 07.00 น. วันที่ 1 พ.ย. นายจินดา กมลธรไท นายกสมาคมธุรกิจท่องเที่ยว จ.เพชรบูรณ์ กล่าวว่า ขณะนี้อากาศในช่วงปลายฝน ต้นหนาวของจ.เพชรบูรณ์ มีสภาพอากาศหนาวเย็นและจะเย็นขึ้นตามลำดับ <STRONG>โดยเฉพาะที่ภูทับเบิกบนเทือกเขาเพชรบูรณ์ สูงกว่าระดับน้ำทะเล 1,800 เมตร ในช่วงเช้าอุณหภูมิจะอยู่ที่ 10 องศาเซลเซียส มีคลื่นทะเลหมอกปกคลุมไปทั่วเป็นบริเวณกว้าง</STRONG> <BR><BR>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <FONT color=#ac002b>ชาวบ้านส่วนมากซึ่งเป็นชาวเขาเผ่าม้งเริ่มนอนผิงไฟกันแล้ว โดยทุกบ้านจะก่อกองไฟไว้ในบ้านเพื่อให้ร่างกายอบอุ่น บางบ้านจะใช้วิธีก่อกองไฟไว้ใต้แคร่สำหรับนอน ถ้าไม่ทำเช่นนี้ร่างกายจะทนกับสภาพอากาศที่หนาวเย็นไม่ได้ ส่วนของนักท่องเที่ยวเริ่มพากันทยอยเดินทางขึ้นไปสัมผัสอากาศหนาวเย็นกันบ้างแล้ว เชื่อว่าในช่วงวันหยุดจะมีมากขึ้น</FONT><BR><BR>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; ส่วนที่อ.เขาค้อ นายจีระศักดิ์ ศักรางกูร นายอำเภอเขาค้อ กล่าวว่า <STRONG>อากาศที่อ.เขาค้อ มีสภาพอากาศที่หนาวอุณหภูมิอยู่ที่ 12-15 องศาเซลเซียส ทุกวัน</STRONG>จะมีนักท่องเที่ยวเริ่มพากันทยอยขึ้นไปพักผ่อนตามรีสอร์ต บ้านพักกันบ้างแล้ว แต่จะมีมากในช่วงที่เป็นวันหยุด ส่วนรีสอร์ตต่างๆ ที่มีอยู่มากมายบางแห่งถูกจองไว้เต็มไปจนถึงเดือนมี.ค.2550<BR><BR>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <FONT color=#0000d7>&nbsp;แต่สำหรับที่กางเต็นท์ยังมีที่ว่างให้กางเต็นท์ได้ ที่ผ่านมามักจะถูกร้องเรียนจากนักท่องเที่ยวว่า อาหาร น้ำดื่ม และที่พักมีราคาแพง ซึ่งเรื่องนี้ตนได้ประชุมชี้แจงให้กับบรรดาผู้ประกอบการให้คำนึงถึงชื่อเสียงของเขาค้อ ถ้าหากนักท่องเที่ยวถูกเอาเปรียบมาก ก็จะพากันไปเที่ยวที่แห่งอื่น จะทำให้อยู่กันไม่ได้ ไม่ควรคิดเอากำไรมาก<BR></FONT><BR>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; ที่อ.น้ำหนาว นายขาว คำมาตร นายอำเภอน้ำหนาว กล่าวว่า สภาพอากาศที่หนาวเย็น อุณหภูมิอยู่ที่ 9-12 องศาเซลเซียส ชาวบ้านส่วนมากมีฐานะยากจน จะพากันออกมาก่อกองไฟผิงกัน บางครั้งก็ต้องพาลูกหลานนอนกันข้างกองไฟ <STRONG>ปกติสภาพอากาศที่น้ำหนาวจะมีอากาศที่เย็นตลอดทั้งปี คาดว่าปีนี้จะหนาวกว่าทุกปีที่ผ่านมา และจะหนาวนาน ชาวบ้านเริ่มประสบกับปัญหาขาดแคลนเสื้อผ้า ผ้าห่ม</STRONG> ซึ่งขณะนี้อำเภอกำลังเตรียมการช่วยเหลืออยู่ ซึ่งคงจะช่วยได้เพียงบางส่วน เพราะคนที่ขาดแคลนมีมาก และส่วนมากยากจน<BR></DIV>', 'ไม่มีข้อมูลลง', 'J8vIBoxhawnamdang1.jpg', '2006-11-03', '', '', '13:55:23', 'chan', 1001, '2', '10003', '0', '', 'y');
INSERT INTO `detail_news` VALUES (10, 'ก.วิทยาศาสตร์ฯ สนองพระราชดำริ ในการจำลองศูนย์ศึกษา การพัฒนามาไว้ใน Bio World จ.ปทุมธานี', '<FONT color=#ac002b>ชาวบ้านส่วนมากซึ่งเป็นชาวเขาเผ่าม้งเริ่มนอนผิงไฟกันแล้ว โดยทุกบ้านจะก่อกองไฟไว้ในบ้านเพื่อให้ร่างกายอบอุ่น บางบ้านจะใช้วิธีก่อกองไฟไว้ใต้แคร่สำหรับนอน </FONT', 'เริ่มหนาวแล้ว-นักเที่ยวแห่ภูทับเบิก<BR><BR>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<BR>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <FONT color=#ac002b>ชาวบ้านส่วนมากซึ่งเป็นชาวเขาเผ่าม้งเริ่มนอนผิงไฟกันแล้ว โดยทุกบ้านจะก่อกองไฟไว้ในบ้านเพื่อให้ร่างกายอบอุ่น บางบ้านจะใช้วิธีก่อกองไฟไว้ใต้แคร่สำหรับนอน ถ้าไม่ทำเช่นนี้ร่างกายจะทนกับสภาพอากาศที่หนาวเย็นไม่ได้ ส่วนของนักท่องเที่ยวเริ่มพากันทยอยเดินทางขึ้นไปสัมผัสอากาศหนาวเย็นกันบ้างแล้ว เชื่อว่าในช่วงวันหยุดจะมีมากขึ้น</FONT><BR><BR>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; ส่วนที่อ.เขาค้อ นายจีระศักดิ์ ศักรางกูร นายอำเภอเขาค้อ กล่าวว่า <STRONG>อากาศที่อ.เขาค้อ มีสภาพอากาศที่หนาวอุณหภูมิอยู่ที่ 12-15 องศาเซลเซียส ทุกวัน</STRONG>จะมีนักท่องเที่ยวเริ่มพากันทยอยขึ้นไปพักผ่อนตามรีสอร์ต บ้านพักกันบ้างแล้ว แต่จะมีมากในช่วงที่เป็นวันหยุด ส่วนรีสอร์ตต่างๆ ที่มีอยู่มากมายบางแห่งถูกจองไว้เต็มไปจนถึงเดือนมี.ค.2550<BR><BR>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <FONT color=#0000d7>&nbsp;แต่สำหรับที่กางเต็นท์ยังมีที่ว่างให้กางเต็นท์ได้ ที่ผ่านมามักจะถูกร้องเรียนจากนักท่องเที่ยวว่า อาหาร น้ำดื่ม และที่พักมีราคาแพง ซึ่งเรื่องนี้ตนได้ประชุมชี้แจงให้กับบรรดาผู้ประกอบการให้คำนึงถึงชื่อเสียงของเขาค้อ ถ้าหากนักท่องเที่ยวถูกเอาเปรียบมาก ก็จะพากันไปเที่ยวที่แห่งอื่น จะทำให้อยู่กันไม่ได้ ไม่ควรคิดเอากำไรมาก<BR></FONT><BR>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; ที่อ.น้ำหนาว นายขาว คำมาตร นายอำเภอน้ำหนาว กล่าวว่า สภาพอากาศที่หนาวเย็น อุณหภูมิอยู่ที่ 9-12 องศาเซลเซียส ชาวบ้านส่วนมากมีฐานะยากจน จะพากันออกมาก่อกองไฟผิงกัน บางครั้งก็ต้องพาลูกหลานนอนกันข้างกองไฟ <STRONG>ปกติสภาพอากาศที่น้ำหนาวจะมีอากาศที่เย็นตลอดทั้งปี คาดว่าปีนี้จะหนาวกว่าทุกปีที่ผ่านมา และจะหนาวนาน ชาวบ้านเริ่มประสบกับปัญหาขาดแคลนเสื้อผ้า ผ้าห่ม</STRONG> ซึ่งขณะนี้อำเภอกำลังเตรียมการช่วยเหลืออยู่ ซึ่งคงจะช่วยได้เพียงบางส่วน เพราะคนที่ขาดแคลนมีมาก และส่วนมากยากจน<BR>', 'ไม่มีข้อมูลลง', '6_small.jpg', '2006-11-03', '', '', '14:03:03', 'chan', 1003, '1', '10003', '0', '', 'y');
INSERT INTO `detail_news` VALUES (11, 'สารรัฐมนตรีว่าการกระทรวงศึกษาธิการ', 'เนื่องในวันเด็กแห่งชาติ ประจำปีพุทธศักราช 2550 ซึ่งตรงกับวันที่ 13 มกราคม 2550 ในนามของข้าราชการกระทรวงศึกษาธิการ		 \r\n		  \r\n		  \r\n		  \r\n		  \r\n		  ', 'ในโอกาสนี้ ขอเชิญชวนผู้ใหญ่ทุกท่านมาช่วยกันส่งเสริมสนับสนุนเด็กและเยาวชนของเรา ให้มีพื้นฐานจิตใจที่ดีงาม มีชีวิตท่ามกลางสังคมสิ่งแวดล้อมที่เปี่ยมด้วยความรัก ความสามัคคี ให้ความรู้ความเข้าใจ การอยู่ร่วมกับผู้อื่นในสังคมตั้งแต่วัยเยาว์ เพื่อที่จะสามารถพัฒนาตนเองในทุกด้านและเจริญเติบโตอย่างมีความสุข\r\n\r\n       ความรักความใส่ใจ ดูแลของพ่อแม่และสมาชิกทุกคนในบ้าน จะเป็นฐานที่มั่นคงให้เด็กได้เรียนรู้และเชื่อมโยงไปสู่โรงเรียน โดยเฉพาะอย่างยิ่ง การประพฤติปฏิบัติตนตามวัฒนธรรมและประเพณีอันดีงามของไทย เป็นการบ่มเพาะนิสัยและสร้างเสริมสติปัญญาให้แก่ลูกหลานซึ่งกระทรวงศึกษาธิการได้เล็งเห็นความสำคัญของเด็กและเยาวชนไทย ซึ่งถือเป็นภารกิจสำคัญที่ทุกฝ่ายจะต้องร่วมมือร่วมใจกัน เพื่อช่วยพัฒนาความพร้อมให้แก่เด็กและเยาวชนตามแนวพระราชดำริปรัชญาเศรษฐกิจพอเพียง และยึดหลักคุณธรรมนำความรู้ โดยการสร้างภูมิคุ้มกันให้แก่เด็กๆในเบื้องต้น เพื่อให้โลกรู้เท่าทันโลกสมัยใหม่ รับรู้และเข้าใจปัญหาสังคม สิ่งแวดล้อมที่เสื่อมโทรม สามารถ ปฏิบัติตามหลักคำสอนของศาสนาที่ตนนับถือ ปรับตัวให้สอดคล้องกับเทคโนโลยีและวิทยาการต่างๆที่รวมทั้งนำไปพัฒนาตนเองในชุมชนให้เป็นสังคมที่เข้มแข็ง ที่คนอยู่เย็นเป็นสุขร่วมกันอย่างสมานฉันท์\r\n\r\n        ผมขอส่งกำลังใจมายังเด็กๆทุกคนจงตั้งมั่นและมั่นคงในการประพฤติตนเป็นคนดี มีน้ำใจ สนใจเรียนรู้อย่างกระตือรือร้น เชื่อฟังพ่อแม่ผู้ใหญ่ครูอาจารย์ ยึดมั่นในคุณธรรม มีความซื่อตรงต่อตนเองและผู้อื่น รวมทั้งการให้เกียรติ ให้ความเคารพนับถือ ผู้อื่นและรู้จักให้อภัย อันเป็นหัวใจสำคัญในการสร้างความรักความสามัคคี และความสมานฉันท์ในหมู่คณะ\r\n\r\n       วันเด็กแห่งชาติปีนี้ ผมขอเชิญชวนเด็กๆ ทุกคนตั้งปณิธานร่วมกันว่า ร้อยรักสามัคคี มีภูมิคุ้มกัน สมานฉันท์ในหมู่คณะ และขออำนวยพรให้แก่เด็กและเยาวชนทุกคนมีสุขภาพพลานามัยสมบุรณ์แข็งแรง มีสติปัญญาเฉลียวฉลาด มีพลังใจที่เข้มแข็ง เพื่อนำพาชีวิตสู่ความสุขและมีอนาคตที่สดใสตลอดไป\r\n		  \r\n		  \r\n		  \r\n		  \r\n		  ', 'ไม่มีข้อมูลลง', 'jQjKBatDSCN4393.JPG', '2006-12-25', '', '', '14:37:26', 'chan', 1003, '1', '10003', '0', '', 'y');
INSERT INTO `detail_news` VALUES (12, 'กิจกรรมการปีนหน้าผา', 'กิจกรรมการปีนหน้าผา เป็นอีกกิจกรรมหนึ่งที่ถูกจัดให้อยู่ในกลุ่มการท่องเที่ยวเชิงนิเวศ เพราะถือว่าเป็นการท่องเที่ยว		 \r\n		  \r\n		  \r\n		  \r\n		  \r\n		  ', 'นอกจากความเพลิดเพลินแล้ว การปีนหน้าผายังเป็นกีฬาอย่างหนึ่งด้วย เพราะร่างกายได้เคลื่อนไหวและออกแรงใน การพยายามที่จะไปให้ถึงจุดสูงสุดที่ตั้งใจเอาไว้ แต่การปีนหน้าผาใช่แต่จะใช้กำลังและความคล่องตัวเพียงเท่านั้น ยังรวม ถึงการมีสมาธิและความมั่นคงในการที่จะผ่านชะง่อนหินและด่านต่าง ๆ ที่สร้างขึ้นโดยธรรมชาติ นอกจากนั้นการตัดสิน ใจที่รอบคอบ ประสบการณ์และความชำนาญก็มีส่วนสำคัญ เพราะการผิดพลาดบางครั้งอาจจะเกิดอาการบาดเจ็บและเป็น อันตราย \r\n\r\n\r\nการปีนหน้าผานั้นอาจจะดูคล้ายการปีนเขาก็จริง แต่มีอันตรายน้อยกว่ามาก โดยบริษัทนำเที่ยวจะมีการทำเครื่องหมาย หรือเครื่องยึด ทำให้สะดวกและง่ายต่อการปีน มีผู้ควบคุมเชือก หรือบีเลเยอร์ (Belayer) ช่วยดึงและแนะนำการปีนอยู่ที่ พื้นด้านล่าง และจะมีการฝึกสอนให้รู้จักเทคนิค อุปกรณ์ และทดลองปีนกับหน้าผาจำลองก่อน \r\n\r\n\r\nในระยะแรก ๆ กลุ่มนักปีนหน้าผาส่วนใหญ่จะเป็นชาวต่างชาติที่เดินทางเข้ามาท่องเที่ยวในประเทศไทย โดยเฉพาะ ที่กระบี่ได้รับการกล่าวขานถึงความสวยงามของสถานที่และความซับซ้อนของหน้าผา ในขณะที่คนไทยยังมองว่าการปีน หน้าผาเป็นกิจกรรมที่อันตราย แต่ในปัจจุบันหลังจากที่กระแสการท่องเที่ยวเชิงอนุรักษ์ได้รับความสนใจจากนักท่องเที่ยว ชาวไทย ก็มีการแสวงหาการท่องเที่ยวแบบใหม่ ๆ ที่เน้นการผจญภัย และความตื่นเต้น เช่น การล่องแก่ง ดำน้ำ ขี่จักรยาน เสือภูเขา และกิจกรรมการปีนหน้าผา ทำให้ปริมาณผู้สนใจ และบริษัทนำเที่ยวที่จัดกิจกรรมในลักษณะนี้เพิ่มมากขึ้น 		  \r\n		  \r\n		  \r\n		  \r\n		  ', 'ไม่มีข้อมูลลง', 'jahG1kXPicture 082,1.jpg', '2007-01-05', '', '', '11:39:59', 'chan', 1003, '1', '10003', '2', '', 'y');
INSERT INTO `detail_news` VALUES (13, '"น้ำตกเต่าดำ"สายน้ำแห่งความท้าทาย', 'ชื่อของ "น้ำตกเต่าดำ" ในอุทยานแห่งชาติคลองวังเจ้า จังหวัดกำแพงเพชร เคยมีคนเล่าขานให้ "ผู้จัดการท่องเที่ยว" 		 \r\n		  \r\n		  \r\n		  \r\n		  \r\n		  ', 'จากกรุงเทพฯเดินทางแบบสบายๆสู่ที่ทำการอุทยานฯคลองวังเจ้า ใช้เวลาประมาณ 4 ชั่วโมง ก่อนที่ทางทีมงานจะให้คณะแยกย้ายกันไปนั่งรถโฟร์วีลของกลุ่มออฟโรดกำแพงเพชรมุ่งหน้าสู่บ้านโละโคะ\r\n       \r\n       ระยะทางช่วงแรกถนนหนทางลาดยางดูดี รถวิ่งฉิวจนเพื่อนร่วมทริปบางคนปรามาสว่าถนนอย่างนี้รถเก๋งก็วิ่งได้สบาย ไม่เห็นจำเป็นจะต้องใช้รถโฟร์วีลด์เลย แต่หลังจากที่เพื่อนคนนั้นพูดได้สักพัก จู่ๆถนนลาดยางก็หายไปกลายเป็นถนนลูกรังที่ทั้งแคบและคดเคี้ยวทอดยาวหายเข้าไปในผืนป่า\r\n       \r\n       ปฐมบทของความวิบากได้เริ่มขึ้นแล้วเมื่อรถแล่นขึ้นๆ ลงๆ คดๆ เคี้ยวๆ ดังงูเลื้อยผ่านป่าไพร หุบเขา ทุ่งนา ไร่ข้าวโพด บ้านคน สลับกันไป บางช่วงเป็นลำธารมีไม้ซุงพาดผ่านให้รถวิ่งข้ามไปอย่างลุ้นระทึก\r\n       \r\n       สำหรับเส้นทางจากที่ทำการอุทยานฯสู่บ้านโละโคะที่ถือเป็นจุดหมายของการค้างแรมในคืนนี้ มีระยะทาง 17 กม. ใช้เวลาเดินทางราว 1 ชั่งโมงครึ่ง ซึ่งกว่าจะเดินทางถึงบ้านโละโคะ ตัวของเรานอกจากจะหัวสั่นหัวคลอนตัวกระดอนกระเด็นไปตามแรงเหวี่ยงของรถและสภาพของถนนแล้ว "ผู้จัดการท่องเที่ยว" ยังรู้สึกว่าเครื่องในของเรามันได้สลับสับเปลี่ยนตำแหน่งไปตั้งแต่เมื่อรถเทโค้งที่ 3 แล้วล่ะ\r\n		  \r\n		  \r\n		  \r\n		  \r\n		  ', 'ไม่มีข้อมูลลง', 'fqBsd8t1589.jpg', '2007-01-05', '', '', '14:16:51', 'chan', 1003, '1', '10003', '0', '', 'y');
INSERT INTO `detail_news` VALUES (14, ' สะพานข้ามแม่น้ำแคว', 'เป็นสถานที่ทางประวัติศาสตร์ที่สำคัญยิ่งแห่งหนึ่ง\r\nสร้างขึ้นสมัยสงครามโลกครั้งที่ 2 		 \r\n		  \r\n		  \r\n		  \r\n		  \r\n		  ', 'เป็นสถานที่ทางประวัติศาสตร์ที่สำคัญยิ่งแห่งหนึ่ง\r\nสร้างขึ้นสมัยสงครามโลกครั้งที่ 2 โดยกองทัพญี่ปุ่นได้เกณฑ์เชลยศึกพันธมิตร \r\nได้แก่ ทหารอังกฤษ อเมริกัน ออสเตรเลีย และฮอลันดา จำนวนมากมา สร้างทางรถ\r\nไฟสายยุทธศาสตร์ผ่านประเทศพม่า ซึ่งมีส่วนหนึ่งจะต้องข้ามแม่น้ำแควใหญ่ การสร้างสะพานและทางรถไฟสายนี้เป็นไปด้วยความยากลำบากเพราะความทารุณของ\r\nสงครามและโรคภัยตลอดจนขาดอาหาร ทำให้เชลยศึกจำนวนหลายหมื่นคนต้องเสียชีวิตลง\r\nสะพานข้ามแม่น้ำแควตั้งอยู่ที่ตำบลท่ามะขาม ห่างจากตัวเมืองไปทางทิศเหนือตามทางหลวงหมายเลข\r\n323 ประมาณ 4 กิโลเมตร แยกซ้ายประมาณ 400 เมตร มีป้ายเขียนบอกไว้ชัดเจน\r\n		  \r\n		  \r\n		  \r\n		  \r\n		  ', 'ไม่มีข้อมูลลง', 'BG5QBGN06-1.jpg', '2007-01-05', '', '', '15:08:10', 'chan', 1003, '1', '10003', '0', '', 'y');
INSERT INTO `detail_news` VALUES (15, 'บ่อพลอย', 'บ่อพลอย อยู่ห่างจากตัวเมืองกาญจนบุรีประมาณ 48 กิโลเมตร โดยสามารถเดินทางไปได้ 2 เส้นทาง 		 \r\n		  \r\n		  \r\n		  \r\n		  \r\n		  ', 'บ่อพลอย อยู่ห่างจากตัวเมืองกาญจนบุรีประมาณ 48 กิโลเมตร โดยสามารถเดินทางไปได้ 2 เส้นทาง คือ ไปตามเส้นทางกาญจนบุรี-ลาดหญ้า ถึงกิโลเมตรที่ 17 มีทางแยกไปบ่อพลอยอีก 31 กิโลเมตร หรือเมื่อผ่านตัวจังหวัดกาญจนบุรีไปประมาณ 7 กิโลเมตร จะมีทางแยกเลี้ยวซ้ายไปตามเส้นทางกาญจนบุรี-ทุ่งมะสังข์ ขนสุดเส้นทาง แล้วจึงเลี้ยวขวาเข้าอำเภอบ่อพลอย ระหว่างทางที่ผ่านเขตอำเภอบ่อพลอยจะเห็นเหมืองพลอยอยู่ทั่วไป ในตัวอำเภอบ่อพลอยมีร้านขายพลอยอยู่หลายร้าน พลอยที่ได้จากการทำเหมืองอุตสาหกรรมได้แก่ พลอยไพลิน นิล และบุษราคัม\r\n\r\n    โครงการพระราชดำริห้วยองคต ตั้งอยู่ที่ตำบลสมเด็จเจริญ เส้นทางหนองปรือ-ด่านช้าง มีพื้นที่กว่าสองหมื่นไร่ ตั้งขึ้นเพื่อการอนุรักษ์และพัฒนาลุ่มน้ำองคต สิ่งที่น่าสนใจในบริเวณนี้ได้แก่ สวนป่า และพลับพลาที่ประทับ นอกจากนี้มีการขุดพบซากโบราณสถาน เครื่องมือเครื่องใช้ของมนุษย์ยุคก่อนประวัติศาสตร์ในบริเวณนี้ด้วย\r\n		  \r\n		  \r\n		  \r\n		  \r\n		  ', 'ไม่มีข้อมูลลง', 'Veb4BQx12.jpg', '2007-01-05', '', '', '15:12:13', 'chan', 1003, '1', '10003', '0', '', 'y');
INSERT INTO `detail_news` VALUES (16, 'สถาบันมาตรวิทยาแห่งชาติ ร่วมมือองค์การ JICA พัฒนามาตรฐานเครื่องมือวัด เพื่อศักยภาพทางวิศวกรรมโยธา', 'เมื่อเอ่ยชื่อ "แปะตำปึง" ขึ้นมา หลายคนอาจจะทำหน้างงๆ พร้อมทั้งนึกไม่ออกว่า "108 เคล็ดกิน" พูดถึงอะไร แต่สำหรับเซียนสมุนไพรหรือผู้ที่ชอบกินพืชผักที่เป็นสมุนไพรคงจะคุ้นหูกันมาบ้าง		 \r\n		  \r\n		  \r\n		  \r\n		  \r\n		  ', 'อันว่า "แปะตำปึง" นั้น เป็นสมุนไพรมาจากประเทศจีน มาตั้งชื่อภาษาไทยกันว่าต้น "จักรนารายณ์" นั่นเอง เมื่อก่อนซื้อขายกันในราคาแพง แต่ปัจจุบันมีปลูกกันมากในเมืองไทยแล้ว ใบแปะตำปึงนี้ว่ากันว่าเป็นสมุนไพรครอบจักรวาล มีสรรพคุณช่วยฟอกเลือด ปรับระบบเลือดและน้ำเหลืองให้ดีขึ้น ทั้งยังช่วยชะล้างสารพิษภายในร่ายกาย นำมากินแก้โรคได้มากมาย ไม่ว่าจะเป็นโรคหัวใจ โรคความดันโลหิต โรคเบาหวาน โรคไขมันอุดตันในเส้นเลือด โรคมะเร็ง\r\n       \r\n       วิธีกินก็แค่เด็ดใบมาสดๆ กินมื้อละ 5-7 ใบ จะกินเป็นผักเครื่องเคียง หรือทำเป็นแกงจืดหมูสับก็ได้ หรือใครจะปั่นกินเป็นน้ำแปะตำปึงก็ได้ผสมน้ำผึ้งนิดหน่อยเพื่อให้รสชาติพอดื่มได้\r\n       \r\n       อีกทั้งยังมีสรรพคุณใช้ภายนอกได้อีกต่างหาก โดยนำมาตำพอกบาดแผล แก้ปวดฝี แก้บวม ฟกช้ำ แก้พิษอักเสบทุกชนิด และถอนพิษจากการถูกสัตว์ที่มีพิษกัดหรือต่อยก็ได้ด้วย		  \r\n		  \r\n		  \r\n		  \r\n		  ', 'ไม่มีข้อมูลลง', '5_small.jpg', '2007-01-22', '', '', '15:53:04', 'admin', 1003, '1', '10002', '0', '', 'y');
INSERT INTO `detail_news` VALUES (17, 'รมว.วิทยาศาสตร์ฯ ยืนยันดาวเทียม สำรวจทรัพยากร ธีออส พร้อม ปล่อยขึ้นสู่วงโคจร เดือนตุลาคมนี้', 'หน้าหนาวทีไร อานิสงส์แห่งอากาศหนาวจะส่งผลให้ดอกไม้แห่งเมืองหนาวพากันผลิดอกชูช่อเบ่งบาน\r\n       \r\n       ในเมืองไทย หน้าหนาวนี้ตามสถานที่ท่องเที่ยวหลายแห่งในภาคเหนือ ต่างก็มีมวลหมู่ดอกไม้งามหลากหลายชนิดพากันผลิดอกชูช่อไสว รอคอยนักท่องเที่ยวให้ไปเที่ยวชม\r\n       \r\n       และนี่ถือเป็นบางส่วนของไฮไลท์ดอกไม้ยามหน้าหนาวในภาคเหนือ ที่รอคอยให้ผู้ที่ชื่นชอบในมนต์เสน่ห์แห่งดอกไม้เดินทางไปสัมผัสในความงาม\r\n		 \r\n		  \r\n		  \r\n		  \r\n		  \r\n		  ', '"ชมพูภูคา"หนึ่งในโลกที่ดอยภูคา\r\n       \r\n       ที่อุทยานแห่งชาติดอยภูคา จ.น่าน ถือเป็นถิ่นอาศัยของต้น"ชมพูภูคา" พืชหายากใกล้สูญพันธุ์ ดอกสีชมพูอมขาวงดงาม เมื่อเกือบครึ่งศตวรรษที่ผ่านมาเคยมีรายงานการสำรวจพบต้นชมพูภูคาทางตอนใต้ของประเทศจีนและทางตอนเหนือของประเทศเวียดนาม\r\n       จากนั้นไม่มีรายงานการค้นพบต้นไม้ชนิดนี้อีก ทำให้คาดว่าอาจจะสูญพันธุ์ไปจากโลกแล้ว จนในปี พ.ศ. 2532 ดร.ธวัชชัย สันติสุข นักพฤกษศาสตร์ ได้ค้นพบต้นชมพูภูคาอีกครั้งที่อุทยานแห่งชาติดอยภูคา\r\n       และตอนนี้ก็ใกล้ถึงเวลาที่"ชมพูภูคา"จะออกดอกอวดโฉมความเป็นหนึ่งเพื่อต้อนรับนักท่องเที่ยวแล้ว ระหว่างเดือน ก.พ.-มี.ค. เป็นช่วงที่ดอกชมพูภูคาพร้อมใจกันเบ่งบาน โดยมีรูปลักษณ์คล้ายระฆัง กลีบดอกด้านนอกมีสีชมพูจางขาว และกลีบดอกด้านในมีสีชมพูลายเส้นสีม่วง ชูช่อเป็นพวงใหญ่\r\n       ซึ่งอุทยานแห่งนี้นอกจากจะมีดอกชมพูภูคาเป็นไฮไลท์แล้วก็ยังมีจุดท่องเที่ยวที่น่าสนใจอีกหลายอย่างเช่น เส้นทางศึกษาธรรมชาติ ซึ่งมีระยะทางประมาณ 3.5 กิโลเมตร มีจุดเริ่มต้นบริเวณที่ทำการอุทยานฯ โดยในเส้นทางศึกษาธรรมชาตินี้มีต้นชมพูภูคาให้เยี่ยมชม แล้วยังมี"ต้นเต่าร้างยักษ์" และ"ก่วมภูคา" ให้ชมอีกด้วย\r\n       \r\n      \r\n       จากน่านขึ้นไปที่จังหวัดเชียงใหม่ ณ ยอดดอยอินทนนท์ ในอุทยานแห่งชาติอินทนนท์ ซึ่งเป็นยอดเขาที่ขึ้นชื่อว่าสูงที่สุดในประเทศไทย ที่มีอากาศหนาวเย็นปกคลุมตลอดปี ที่แห่งนี้ "กุหลาบพันปี" ไม้งามแห่งหิมาลัยปรากฏโฉมท้าลมหนาวอยู่ในช่วงเดือนธ.ค.-ม.ค.ของทุกปี\r\n       \r\n       กุหลาบพันปีที่พบบนดอยอินทนนท์นั้น คือ Rhododendron Arborea เป็นชนิดเดียวกันบนหิมาลัย ซึ่งเป็นดอกไม้ประจำชาติของเนปาล นักท่องเที่ยวที่มายลโฉม กุหลาบพันปี จะสามารถพบเจอได้ที่บริเวณอ่างกา และผาแง่ม\r\n       ซึ่งมีทั้งพันธุ์ดอกสีขาวที่ชื่อท้องถิ่นเรียกกันว่า"คำขาว" และดอกสีแดงเข้มหรือ"คำแดง"นับเป็นพืชหายากที่มีดอกงดงามมาก ชนิดหนึ่งของประเทศไทยและจะพบอยู่เฉพาะในเขตอากาศหนาวเย็นบนพื้นที่ ชุ่มชื้น สันเขาหรือหน้าผาระดับความสูงประมาณ 1,600 - 2,500 เมตร เหนือระดับน้ำทะเล\r\n       \r\n       กุหลาบพันปีได้รับยกย่องว่าเป็นราชินีแห่งเทือกเขาหิมาลัย หลายๆคนเข้าใจผิดว่า กุหลาบพันปีบนดอยอินทนนท์คือกุหลาบโบราณ ความจริงแล้ว เหตุที่เรียกกันว่า กุหลาบพันปี ก็เพราะว่าไม้ชนิดนี้ยามออกดอกจะมีสีสันสวยงามเหมือนดอกกุหลาบ นอกจากนี้ลำต้นที่หงิกงอ มีมอส ไลเคนขึ้นปกคลุมจึงเป็นอีกสาเหตุหนึ่งที่ทำให้ดูเก่าแก่โบราณดังกุหลาบพันปี\r\n\r\n       ทิวลิปหลากสีที่"ดอยผาหม่น"\r\n       \r\n       ส่วนแหล่งชมดอกไม้หน้าหนาวที่กำลังมาแรงในช่วงนี้ก็คงจะหนีไม่พ้นที่ ณ ศูนย์ส่งเสริมการเกษตรที่สูงดอยผาหม่น บ้านพญาเลาอู ต.ตับเต่า อ.เทิง จ.เชียงราย ซึ่งหลายๆคนอาจจะยังไม่รู้ว่าที่นี่เป็นสถานที่ซึ่งได้ชื่อว่าเป็นแหล่งเพาะพันธุ์ดอกทิวลิปที่มากที่สุดในประเทศไทย\r\n       โดยในช่วงเดือนธันวาคม-มกราคม ดอกทิวลิปหลายหลากสีจะพากันเบ่งบานสลอนเต็มที่ เพราะอุณหภูมิบนดอยผาหม่นในช่วงหน้าหนาวที่อยู่ประมาณ 2-8 องศาเซลเซียสนั้น เหมาะต่อการบานของดอกทิวลิป\r\n       \r\n       ทั้งนี้ศูนย์ฯดอยผาหม่นเป็นแหล่งเพาะพันธุ์ทิวลิปชั้นดีจากประเทศเนเธอแลนด์ ด้วยโครงการปลูกดอกไม้เมืองหนาว อาทิ ดอกทิวลิป ดอกลิลลี่ โดยใช้หัวพันธุ์กว่า 20,000 หัว มาหลายปีดีดักแล้ว สำหรับดอกทิวลิปมีทั้งสิ้น 6 สายพันธุ์ คือ Christmas Sweet (สีขาว) LLe de Srance (สีแดง) Aaske (สีชมพู) Leen Vd Nark (สีแดงเข้ม) Negrita (สีม่วง) และ Strong gold (สีเหลืองทอง)\r\n       \r\n       นักท่องเที่ยวที่เข้าไปเยือนศูนย์ฯดอยผาหม่น จะได้พบกับ ดอกทิวลิปหลากสี รวมถึงดอกลิลลี่ และดอกไม้เมืองหนาวอีกมากมายบนเนื้อที่กว่า 5 ไร่ โดยบางวันคนรอชมแน่นขนัดทางเจ้าหน้าที่ก็จะให้ต่อแถวเข้าชม แต่ถ้าวันไหนคนน้อยก็สามารถเข้าชมกันได้สบายๆ โดยไม่ต้องเสียค่าใช้จ่ายใดๆ นอกจากนี้ที่บริเวณหน้าศูนย์ฯดอยผาหม่น นักท่องเที่ยวยังร่วมถ่ายภาพประทับใจกับเด็กๆชาวเขาได้อีกด้วย\r\n       \r\n       สำหรับหนาวนี้ทางสำนักงานการท่องเที่ยวจังหวัดเชียงรายให้ข้อมูลว่าทางดอยผาหม่นจะมีการนำทิวลิปมาลงแปลงให้ชมกันเป็นงวดสุดท้ายในวันที่ 27 มกราคม นี้ โดยสามารถดูได้หลังจากนั้นอีก 15 วัน ก่อนที่ดอกทิวลิปจะร่วงโรย\r\n\r\n \r\n       ดอกเสี้ยวบานตระการตาบน"ภูชี้ฟ้า"\r\n       \r\n       เหนือขึ้นไปจากดอยผาหม่นอีกไม่ไกลในเส้นทางเดียวกันบนยอดดอย "ภูชี้ฟ้า"ช่วงเดือนมกราคม ? กุมภาพันธ์ นอกจากจุดชมพระอาทิตย์ขึ้นและวิวทะเลหมอกอันเลื่องชื่อแล้ว บนภูชี้ฟ้ายังเป็นช่วงที่ดอกเสี้ยวบานขาวโพลนเป็นเวิ้งทั่วป่ารอบเชิงเขาอีกด้วย\r\n       \r\n       "ดอกเสี้ยว"เป็นพันธุ์ไม้ยืนต้นหรืออีกชื่อหนึ่งคือ"ชงโคป่า"นิยมขึ้นตามภูเขาเทือกเขาดอยผาหม่น สวยสะพรั่งบนยอดดอยเป็นสัญลักษณ์แห่งความสวยงามอีกหนึ่งเสน่ห์ของภูชี้ฟ้า ในช่วงเดือนกุมภาพันธ์ระหว่างวันที่ 13-15 ของทุกปี จะมีการจัดงานเทศกาลดอกเสี้ยวบาน\r\n        แต่ก่อนและหลังจากนั้นประมาณ 1 สัปดาห์นักท่องเที่ยวก็สามารถขึ้นไปเยี่ยมชมได้ เพราะเป็นช่วงที่ดอกเสี้ยวผลิบานทั่วขุนเขา ซึ่งนอกจากจะมีความงดงามแล้วชาวบ้านทางภาคเหนือยังนิยมนำมาดัดแปลงปรุงรสเป็นอาหารได้อีกด้วย\r\n\r\n \r\n       "ซากุระ"บานสะพรั่งบนดอยสูง\r\n       \r\n       ปิดท้ายความงามยามหน้าหนาวกันที่ ดอยวาวี อ.แม่สรวย จ.เชียงราย ศูนย์บริการวิชาการด้านพืชและปัจจัยการผลิตเชียงราย(สถานีทดลองเกษตรที่สูงวาวี) บน"ดอยช้าง" โดยช่วงระหว่างเดือน ม.ค.-ก.พ. จะมีดอกซากุระบานย้อมบริเวณที่ปลูกเป็นสีชมพูสดใส\r\n       \r\n       สำหรับซากุระที่ดอยวาวีเป็นซากุระเมืองไทย หรือนางพญาเสือโคร่ง หรือชมพูภูพิงค์ เป็นพืชที่อยู่ในวงศ์ ROSACEAE ตระกูลเดียวกับ ท้อ บ๊วย พลัม เชอร์รี่ หรือซากุระเป็นพันธุ์ไม้ป่าขึ้นบนพื้นที่สูงจากระดับน้ำทะเลปานกลาง 800-1,000 เมตร พบได้ทั่วไปตามยอดดอยในภาคเหนือ อาทิ เชียงใหม่ เชียงราย แม่ฮ่องสอน และน่าน มีทั้งดอกสีชมพู ชมพูประแดง และสีขาว\r\n       แต่ส่วนใหญ่จะเป็นดอกสีชมพู โดยที่ดอยวาวีถือเป็นแหล่งซากุระที่มากที่สุดในเมืองไทย เพราะมีปลูกมากถึง 400,000 ต้น นอกจากนี้ยังมีดอกซากุระสีขาวรวมอยู่ด้วย โดยเมื่อถึงเวลาอันเหมาะสมต้นซากุระริมถนนจะพากันผลิดอกบานเต็ม 2 ข้างทาง\r\n       \r\n       นอกจาก 2 ดอย ที่กล่าวมาแล้ว ทางภาคเหนือยังมีดอกซากุระ(เมืองไทย)บานอีกในหลายพื้นที่ ไม่ว่าจะเป็นที่ ดอยแม่สลอง ดอยอ่างขาง ดอยอินทนนท์ ขุนวาง แม่จอนหลวง ซึ่งความงามของดอกซากุระแต่ละแห่งต่างก็ช่วยสีสันและมนต์เสน่ห์ของหน้าหนาวนี้ให้ดูโรแมนติกน่าเที่ยวมากขึ้น \r\n		  \r\n		  \r\n		  \r\n		  \r\n		  ', 'ไม่มีข้อมูลลง', '4_small.jpg', '2007-01-22', '', '', '15:59:32', 'admin', 1003, '1', '10002', '0', '', 'y');
INSERT INTO `detail_news` VALUES (18, 'สนง.นวัตกรรมแห่งชาติ ผลักดันแผนที่นำทาง เพื่อยกระดับอุตสาหกรรม พลาสติกชีวภาพ ในระยะเวลา 5 ปี', 'จากเหตุการณ์ทะเลวิปโยคคลื่นยักษ์สึนามิถั่งโถมเมื่อวันที่ 26 ธันวาคม 2547 ส่งผลให้ทะเลฝั่งอันดามันเกิดความเสียหายขึ้นในหลายพื้นที่ โดยหนึ่งในพื้นที่ที่โดนคลื่นสยักษ์สึนามิถั่งโถมโหมกระหน่ำก็คือ"เกาะพีพี" มรกตแห่งอันดามัน สถานที่ท่องเที่ยวอันงดงามและโด่งดังแห่งท้องทะเล จังหวัดกระบี่		 \r\n		  \r\n		  \r\n		  \r\n		  \r\n		  ', 'มาวันนี้ 2 ปีผ่านไป สภาพเกาะพีพี มีหลายๆอย่างเปลี่ยนแปลงไป ซึ่งใครที่เคยมาเที่ยวที่เกาะแห่งนี้จะทราบดีว่า เมื่อขึ้นถึงท่าเรือบนเกาะพีพีแล้วเดินตรงมาจะเจอร้านสะดวกซื้อ 24 ชั่วโมง ตั้งเด่นหราเตะตารับนักท่องเที่ยว แต่หลังจากสึนามิได้โถมเข้าใส่ก็ทำให้ร้านสะดวกซื้อพังทลายไปโดยยังคงทิ้งซากให้เห็นอยู่ ส่วนร้านในปัจจุบันนั้นได้ย้ายไปอยู่ใจกลางเกาะเรียบร้อยแล้ว\r\n       \r\n       นอกจากนี้ยังมีอีกหลายๆพื้นที่ที่ยังคงหลงเหลือซากระเกะระกะอยู่บ้าง แต่ในอีกหลายๆพื้นที่ก็ได้มีการเปลี่ยนแปลงปรับปรุงฟื้นฟู รวมไปถึงโรงแรมหลายแห่งที่กำลังจะผุดขึ้นมาใหม่เพื่อรองรับนักท่องเที่ยวที่คาดว่าจะกลับมาคึกคักเหมือนเดิม\r\n\r\n \r\n  \r\nสวนดอกไม้ในบริเวณบ่อบำบัดน้ำเสียแบบบึงประดิษฐ์ \r\n \r\n \r\n       สมยศ บุญสบ อาชีพ Water Sport และเก้าอี้เช่านั่งชายหาดบนเกาะพีพี วัย25 ปี เล่าว่า ตัวเองโตมาที่เกาะยาว และย้ายมาอยู่ที่เกาะพีพีก่อนเกิดเหตุสึนามิ 5 ปี ในช่วงก่อนเกิดภัยพิบัตินั้นนักท่องเที่ยวและผู้คนที่มาทำมาหากินบนเกาะแห่งนี้แน่นจนเดินชนไหล่กัน บาร์ ร้านอาหาร รีสอร์ทก็เนืองแน่นตามมา แต่เมื่อเกิดเหตุการณ์ที่ไม่คาดฝันเมื่อวันที่ 26 ธันวาคม 2547 ที่ผ่านมา นักท่องเที่ยวก็ลดลงอย่างเห็นได้ชัด เรือ speed boat ที่ตนเองเคยขับหลังจากสึนามิถล่มเกาะ เรือก็พังหมดยกเว้นลำที่อยู่นอกฝั่ง แม้แต่พี่เขยของตนเองที่อยู่บริเวณชายหาดก็วิ่งหนีคลื่นยักษ์ไม่ทันจึงต้องสังเวยเสียชีวิตให้กับสึนามิ\r\n       \r\n       ในด้านอาชีพทำมาหากิน สมยศกล่าวว่า "หลังสึนามิมีผลกระทบไม่เท่าไหร่ พออยู่ได้ เพราะนักท่องเที่ยวบางคนอยากมาดูว่าหลังจากเหตุการณ์สึนามิผ่านไป เกาะพีพีเป็นอย่างไร หรือบางคนไม่เคยมาที่เกาะแห่งนี้ ก็อยากมาดูว่าสภาพเกาะหลังเกิดเหตุเป็นอย่างไร"\r\n\r\n \r\n  \r\nหลังสึนามิ หอเตือนภัยกลายเป็นสิ่งจำเป็นสำหรับเกาะพีพี \r\n \r\n \r\n       ส่วนการเปลี่ยนแปลงของสภาพบนเกาะหลังสึนามิ สมยศ เล่าว่า แต่ก่อนจะมีต้นไม้เยอะ ตามชายหาดจะร่มรื่นกว่านี้ แต่สึนามิทำให้ต้นไม้ตายปัจจุบันเลยร้อนขึ้น ในทะเลก็เช่นกันแต่ก่อนตามชายฝั่งน้ำตื้นจะมีปะการัง แต่เดี๋ยวนี้มีแต่หินเต็มไปหมดเนื่องจากสึนามิพามา ซึ่งสร้างความลำบากกับเรือมาก อย่างเช่น banana boat ถ้าน้ำเยอะก็ยังสามารถเล่นได้ หากน้ำลดหินโผล่ขึ้นมาก็จบ แต่สำหรับพวกที่เล่นน้ำตามชายฝั่งก็คงไม่มีปัญหา\r\n       \r\n       "หลังสึนามิไม่ได้มีแต่เรื่องเลวร้ายไปทุกอย่าง เพราะเหตุการณ์ครั้งนั้นได้ทำให้น้ำทะเลใส่ขึ้นกว่าเดิม เนื่องจากแต่ก่อนร้านอาหาร บังกะโล รีสอร์ทจะทิ้งน้ำเสียลงทะเล ทำให้คุณภาพของน้ำเสื่อมลง สำหรับตัวเองแม้สึนามิจะมีผลเสียมากมายผู้คนต้องบาดเจ็บล้มตายเป็นจำนวนมาก แต่ถ้าให้เปรียบเทียบทะเลเกาะพีพีระหว่างก่อนและหลังเกิดสึนามิ ผมชอบหลังจากเกิดสึนามิมากกว่า เพราะนักท่องเที่ยวไม่เยอะมาก ไม่มั่ว ถึงจะทำให้ผมมีรายได้น้อยลงไปบ้าง แต่ก็ทำให้สภาพแวดล้อมดูดีขึ้น" สมยศกล่าว\r\n\r\n \r\n  \r\nกองซากหินปะการังตายแล้วระเกะระกะยามน้ำลด \r\n \r\n \r\n       ด้าน เจ้าของร้าน sun flower ร้านอาหารและบาร์บนเกาะพีพี หนึ่งในผู้ผ่านประสบการณ์คลื่นยักษ์สึนามิ เล่าว่า ร้าน sun flower ร้านเดิมได้ถูกฤทธิ์ของคลื่นยักษ์สึนามิพัดทำลายจนราบเป็นหน้ากลอง ปัจจุบันได้สร้างร้านขึ้นมาใหม่จากซากเศษไม้ที่ถูกพัดมากับคลื่นยักษ์ แม้ตอนนี้เหตุการณ์จะดีขึ้น แต่หลายๆอย่างบนเกาะก็ยังไม่เข้าที่เข้าทาง จำนวนลูกค้าก็หดหายไปมาก และยังมีซากปรักหักพังยังมีให้เห็นอยู่บ้างในบางจุดบนเกาะพีพี\r\n       \r\n       "ในส่วนของความเปลี่ยนแปลงในทะเลที่เห็นได้ชัดก็คือ ตอนน้ำลดจะเห็นพวกหินปะการังสีดำที่ตายแล้ว ไม่มีประโยชน์ใดๆ และยังสร้างความเดือดร้อนให้กับนักเดินเรือ และอาชีพ water sport เช่น banana boat เป็นอย่างมากอีกด้วย เพราะเมื่อก่อนตอนน้ำขึ้นจะขับเรือแล่นได้ตลอดแนว แต่ในตอนนี้การเดินเรือรวมถึงการจอดเรือตามชายฝั่งก็ลำบากมากขึ้นเพราะต้องระมัดระวังโขดหินเหล่านี้ ทั้งยังทำให้ทัศนียภาพไม่สวยงาม ซึ่งทางตนและกลุ่มเพื่อนก็หารือกันว่าอยากจะเคลื่อนย้ายหินเหล่านี้ แต่ด้วยงบประมาณและเครื่องทุ่นแรงที่ขาดแคลน ตอนนี้จึงยังไม่สามารถทำอะไรได้" เจ้าของร้าน sun flower กล่าว\r\n\r\n \r\n  \r\nแม้สึนามิจะผ่านมา 2 ปี แต่หลายๆสิ่งบนเกาะพีพีก็ยังไม่เข้าที่เข้าทาง \r\n \r\n \r\n       นอกจากนี้ เพื่อเป็นการรำลึกถึงความสูญเสียที่เกิดขึ้นในครั้งนั้น ทางร้าน sun flower และเพื่อนๆจึงได้สร้าง memorial garden ขึ้นใกล้ๆกับร้าน sun flower มีลักษณะเป็นสวนเล็กปลูกไม้ดอกไม้ประดับสวยงาม และมีป้ายชื่อผู้เสียชีวิตจากเหตุการณ์สึนามิไว้เป็นอนุสรณ์ และยังจัดพื้นไว้สำหรับร่วมรำลึกด้วย\r\n       \r\n       สำหรับความเปลี่ยนแปลงที่เห็นได้ชัดอีกอย่างหนึ่งบนเกาะพีพีนอกจากหอเตือนภัยสึนามิที่ตั้งตระหง่านแล้ว ยังมีบ่อบำบัดน้ำเสีย ในพื้นที่ขนาดใหญ่ประมาณ 6,300 ตารางเมตร ซึ่งใช้พื้นที่บ่อบำบัดน้ำเสียเดิม ใกล้กับทางขึ้นจุดชมวิว(view point) ที่บริเวณตรงนี้เคยเป็นสุสานของนักท่องเที่ยวและชาวบ้านนับร้อยศพ ที่โดนคลื่นยักษ์ซัดตกลงไปในบ่อ เมื่อเห็นบ่อแห่งนี้เมื่อไรก็สร้างความเจ็บปวดให้กับผู้คนที่รอดชีวิต จึงได้จัดสร้างบ่อบำบัดน้ำเสียใหม่ขึ้นเป็นลักษณะแบบบึงประดิษฐ์\r\n\r\n \r\n  \r\nแท่นคำไว้อาลัยใต้น้ำแห่งเกาะพีพี  \r\n  \r\n \r\n       บ่อบำบัดน้ำเสียแบบบึงประดิษฐ์นี้จะช่วยบำบัดน้ำเสียจากบริเวณต่างๆ ทั้งจากย่านธุรกิจการค้าและโรงแรมรีสอร์ทต่างๆบนเกาะ โดยปลูกต้นไม้หลากหลายชนิดที่ช่วยในการบำบัดน้ำเสีย เช่น หญ้าแฝก ต้นพุทธรักษา เป็นต้น และยังใช้พื้นที่ตรงบริเวณบ่อบำบัดน้ำเสียเป็นสวนสาธารณะที่สวยงามด้วยต้นไม้ดอกไม้นานาพันธุ์ และศาลานั่งเล่น เพื่อช่วยให้ชาวบ้านและนักท่องเที่ยวลืมภาพบ่อที่มีแต่ศพ และยังกลายเป็นสถานที่พักผ่อนหย่อนใจที่สวยงามได้อีกด้วย\r\n       \r\n       นอกจากอนุสรณ์บนบกแล้ว ใต้น้ำแห่งเกาะพีพีก็มีอนุสารีย์สึนามิเช่นกันบริเวณอ่าวต้นไทร โดยรูปแบบของอนุสาวรีย์จึงถูกออกแบบให้มีลักษณะเป็นแท่นหินแกรนิต 3 แท่น เรียงตัวกันเป็นรูปสามเหลี่ยมด้านเท่า ห่างกัน 5,395 เซนติเมตร โดยระยะห่างนี้มีความหมายแอบแฝงหมายถึงจำนวนผู้เสียชีวิตที่สามารถระบุชื่อและสัญชาติได้\r\n       \r\n       บนแท่นหินแกรนิตจารึกคำไว้อาลัยจากจากสถานทูตต่างๆ จำนวน 11 แห่ง ใต้ฐานของแท่นหินแกรนิต จารึกปรัชญาชีวิตจากนักปราชญ์ของชนชาติต่างๆเพื่อเป็นเครื่องเตือนใจในการดำรงชีวิตของมนุษย์ส่วนแท่นหินแกรนิตตรงกลาง จะบอกเล่าเรื่องราวของเหตุการณ์ในวันอาทิตย์ที่ 26 ธันวาคม พ.ศ.2547 ที่ผ่านมา ซึ่งถึงแม้ว่าความทรงจำในวันอันโหดร้ายจากเหตุการณ์สึนามิที่เกาะพีพีและที่อื่นๆ ยังคงแจ่มชัดสำหรับใครหลายๆคน แต่ในเมื่อชีวิตไม่สิ้นก็ต้องดิ้นรนต่อสู้กันต่อไป\r\n \r\n		  \r\n		  \r\n		  \r\n		  \r\n		  ', 'ไม่มีข้อมูลลง', '3_small.jpg', '2007-01-22', '', '', '16:23:35', 'admin', 1003, '1', '10002', '0', '', 'y');
INSERT INTO `detail_news` VALUES (19, 'กระทรวงวิทยาศาสตร์ฯ ร่วมมือ กระทรวงอุตสาหกรรม วิพากษ์ร่าง แผนแม่บทโครงสร้างพื้นฐาน ทางปัญญา', 'หน้าร้อนอย่างนี้เห็นทีใครๆ ก็ต่างเก็บข้าวเก็บของแพ็คใส่กระเป๋าออกเดินทางไปดับร้อน พักผ่อนกายาให้ชุ่มชื่นฉ่ำใจกันทั้งนั้น แล้วสถานที่เที่ยวยอดฮิตติดลมบนของนักเดินทางหลายๆ คนที่เมื่อยามลมร้อนพัดผ่านมากระทบผิวกาย เป็นต้องนึกถึงสายลมเย็นๆ และผืนผิวน้ำอันเย็นใส ที่พร้อมจะกระโจนตัวและกระโจนใจให้โลดแล่นไปให้สมอารมณ์หมาย		 \r\n		  \r\n		  \r\n		  \r\n		  \r\n		  ', '?ผู้จัดการท่องเที่ยว? เป็นหนึ่งในจำนวนผู้รักการเดินทางท่องเที่ยวเมืองไทย ที่เมื่อหน้าร้อนลมร้อนเริ่มมาเมื่อใด สถานที่ที่นึกถึงเป็นอันดับต้นๆ ในหัวสมองก็ไม่พ้นท้องทะเลอันสวยงามของเมืองไทยเช่นกัน ยิ่งช่วงหน้าร้อนอย่างนี้ดูเหมือนว่าท้องทะเลบ้านเราจะสวยใสไฉไลเป็นพิเศษ\r\n       \r\n       เชื่อไม่เชื่ออันนี้ก็ต้องไปพิสูจน์กันเอง อย่างที่ เมื่อช่วงเริ่มต้นลมร้อนที่ผ่านมา ?ผู้จัดการท่องเที่ยว? แอบหนีร้อนไปพึ่งเย็นที่จังหวัดตรัง (ไม่ได้ไปพึ่งบารมีท่านอดีตนายกชวนหรอกนะ!!!) แต่ที่ไปเมืองตรังคราวนี้หวังจะไปหลบร้อนพักผ่อนกายใจขอทำตัวเป็นปลาน้อยแหวกว่ายหาความเย็นจากท้องทะเลกันเสียหน่อย\r\n\r\n  \r\n       และการเดินทางก็มาถึงเมื่อ ?ผู้จัดการท่องเที่ยว? พร้อมกับเพื่อนร่วมทริปพร้อมใจกันออกเดินทางตะลุยท้องน้ำผืนทะเลอันกว้างใหญ่ของจังหวัดตรังกันทันที โดยการไปเที่ยวทะเลตรังของเราในครั้งนี้ เรามีจุดมุ่งหมายเดียวกันทั้งทริปว่าจะไปเข้าถ้ำกัน\r\n       \r\n       สำหรับถ้ำที่พวกเราจะเดินทางไปเที่ยวกันในครั้งนี้ ถือเป็นว่าเป็นถ้ำที่ติดหนึ่งในอันซีนไทยแลนด์ของเมืองไทยที่ทางการท่องเที่ยวแห่งประเทศไทย (ททท.) เขาประกาศไว้ ก็คือ ?ถ้ำมรกต? ถ้ำสวรรค์ที่นักเดินทางหลายๆ คนที่พิสมัยความงามของธรรมชาติโดยเฉพาะความงามของท้องทะเล เป็นต้องหาโอกาสทองดีๆ สักครั้งในชีวิตไปสัมผัสให้ได้ด้วยตนเอง\r\n\r\n \r\n       พวกเราออกเดินทางกันตั้งแต่แสงอาทิตย์เริ่มรำไรไปลงเรือที่ท่าเทียบเรือบ้านเจ้าไหม ลงเรือสปีดโบตลำน้อยแต่ว่าแรงด้วยกำลังขับเคลื่อน จากท่าเทียบเรือเราใช้เวลาการเดินทางไม่นานมากนัก (เพราะยังไม่ทันจะได้หลับ คนขับเรือก็ตะโกนบอกว่าจะถึงเกาะมุกที่เห็นลิบๆ ข้างหน้านั่นแล้ว) และเพียงไม่กี่อึดใจเรือลำน้อยก็จอดนิ่งบนเกลียวคลื่นที่ตอนนี้รอบข้างมีแต่ผืนน้ำสีเขียวใส และก็เกาะมุกที่ตั้งเด่นตระหง่านอยู่เบื้องหน้า แถมด้วยฝูงชนนักท่องเที่ยวกลุ่มใหญ่ทั้งฝรั่งทั้งคนไทย หนาตาไปหมด (ก็อย่างว่าเที่ยวหน้าไฮซีซั่น ต้องทำใจ!!)\r\n       \r\n       แล้วการปฏิบัติภาระอันยิ่งใหญ่ก็เกิดขึ้น เมื่อไกด์นำทางเริ่มบอกให้ทุกคนเตรียมตัว เพราะ ?ถ้ำมรกต? ที่ทุกคนใฝ่ฝันที่จะเข้าไปชมความงามอยู่เบื้องหน้าเรานี่แล้ว ?เห็นโพรงกว้างๆ ที่คนเยอะๆ หน้าปากถ้ำนั่นไหม นั่นละคือเส้นทางที่จะนำพวกเราเข้าสู่ตัวถ้ำมรกตกัน?\r\n       \r\n       ว่าแล้วจะรอช้าอยู่ได้อย่างไร หัวใจมันเต้นตุ๊บๆ ไม่เป็นจังหวะไปหมดแล้ว ก็เป็นครั้งแรกในชีวิตที่จะต้องเข้าถ้ำแถมเข้าแบบไม่ธรรมดา เพราะเห็นไกด์ว่าเราต้องปั่นจักรยานเข้าไป หลายคนอาจะงง!! นี่เราอยู่บนผืนน้ำแล้วจะมีจักรยานที่ไหนให้ปั่น นั่นเป็นคำถามที่ ?ผู้จัดการท่องเที่ยว? ถามตัวเองอยู่ในใจ\r\n\r\n \r\n \r\n       เมื่อพวกเราใส่เสื้อชูชีพพร้อมกันแล้ว ทุกคนต่างก็ทยอยลงน้ำกันทีละคน แล้วเอามือจับไหล่ต่อแถวกันเป็นขบวนยาวพร้อมๆ กับไต่ไปตามแนวเชือกเพื่อเข้าสู่หน้าปากถ้ำกัน\r\n       \r\n       ?พร้อมนะทุกคน จับไหล่กันให้มั่นอย่าทำมือหลุดจากกันละ เดี๋ยวจะพลัดหลงทางกันในถ้ำ เพราะในถ้ำมืดมากไม่มีแสงสว่างเลยนะจะบอกให้? ไกด์นำทางบอกกับพวกเราก่อนเข้าถ้ำด้วยท่าทีขึงขังเอาจริงเอาจังน่าดู\r\n       \r\n       ถึงตรงนี้ดูเหมือนทุกคนจะตื่นเต้นกันใหญ่ปนความกลัวนิดๆ เพราะตอนนี้ในผืนน้ำสีเขียวอันกว้างใหญ่ไม่มีอะไรเลย นอกจากพวกเราที่ใส่เสื้อชูชีพลอยคออยู่กันเป็นแถว กับโพรงกว้างๆ เบื้องหน้าที่พอจะเห็นได้ว่าข้างในต้องมืดจริงๆ แน่\r\n       \r\n       ?เอ้าออกแรงปั่นได้ บึ้ดจ้ำบึ้ดจ้ำบึ้ดจ้ำบึ้ด? เสียงไกด์ส่งสัญญาณให้พวกเราออกแรงปั่นจักรยาน ก็คือการเอาขากระทุ้งน้ำเพื่อให้เกิดแรงดันนั่นเอง เพราะนั่นคือวิธีที่ดีที่สุดที่จะทำให้ขบวนการเดินทางของเราเคลื่อนที่ไปข้างหน้าได้ หลายคนจึงออกแรงปั่นจักรยานอย่างเต็มแรงสุดความสามารถ จนบางครั้งปั่นแรงไปหน่อยหรือเปล่าไม่รู้? เห็นแต่คนข้างหลังแอบทำหน้าเจ็บๆ จุกๆ แบบบอกบุญไม่รับ!!\r\n\r\n \r\n \r\n       จากปากถ้ำด้านหน้าที่ยังพอมีแสงส่องถึงให้เห็นความสว่างอยู่บ้าง แต่พอเคลื่อนตัวเข้าไปในถ้ำเรื่อยๆ ความเงียบและความมืดก็ปกคลุมทุกสิ่งทุกอย่าง ภายในถ้ำมืดไปหมดเราไม่สามารถมองเห็นอะไรได้เลย ถ้าไม่มีแสงไฟจากไฟฉายของไกด์ที่นำทาง มีเพียงแสงไฟที่ริบหรี่และเสียงกระทุ้งน้ำของพวกเราเป็นจังหวะ ขบวนของพวกเราก็ค่อยๆ เคลื่อนตัวไปอย่างช้าๆ\r\n       \r\n       ช่วงระยะเพียงอึดใจเดียว ทุกคนถึงกับตกตลึงร้องเป็นเสียงเดียวกันว่า ?โอ้โห!! สวยจัง มรกตจริงๆ? เมื่อภาพที่พวกเราคือลำแสงที่ปากถ้ำอีกด้านหนึ่ง สาดส่องกระทบกับผิวน้ำทะเลสีเขียวสด กลายเป็นประกายแสงสีเขียวที่ดูเขียวราวกับมรกตจริงๆ สมแล้วกับชื่อ ?ถ้ำมรกต?\r\n       \r\n       แล้วพอพ้นจากปากถ้ำอันเขียวมรกต ความงามของธรรมชาติที่ซ่อนเร้นก็ปรากฏอยู่เบื้องหน้า หาดทรายขาวละเอียดกับผืนน้ำทะเลเขียวใสที่อยู่ภายในถ้ำ แสงอาทิตย์ที่สาดส่องลงมาปากโพรงปล่องถ้ำด้านบน ทำให้?ถ้ำมรกต?กลายเป็นโลกสวรรค์อีกโลกหนึ่งจริงๆ\r\n       \r\n       ความงดงามภายในถ้ำมรกตแห่งนี้นอกจากหาดทรายขาว น้ำทะเลเขียวใสแล้วนั้น เมื่อเรามองไปรอบๆ เหมือนกับว่าเรายืนอยู่ในปล่องขนาดใหญ่ ที่เกิดจากการยุบตัวของหินปูนในอดีต และเรายังได้เห็นถึงความเป็นธรรมชาติที่ยังหลงเหลืออยู่จริงๆ เพราะแม้แต่ป้ายบอกเล่าความเป็นมาของ ?ถ้ำมรกต?ยังต้องเป็นป้ายไม้ เพื่อให้กลมกลืนกับสภาพธรรมชาติ ไกด์แอบกระซิบบอกว่าเมื่อก่อนเป็นป้ายเหล็ก แล้วถูกนักท่องเที่ยวอนุรักษ์ธรรมชาติถอนทิ้งแบบดื้อๆ ด้วยเหตุที่ว่าไม่เข้ากัน กับสภาพธรรมชาติที่มีพันธุ์ไม้ต่างๆ ขึ้นอยู่บริเวณหาดทราย แต่ละพันธุ์ล้วนแล้วแต่เป็นไม้ป่าชายหาด ไม่ว่าจะเป็นต้นหูกวางทะเล เตยทะเล จิกทะเล และบริเวณหน้าผาเป็นป่าหินปูนมีต้นสลัดได จันทน์ผา บอนชนิดต่างๆ\r\n\r\n \r\n       ช่วงเวลาแห่งความสุขนี้พวกเราใช้เวลาดื่มด่ำกับความงามของธรรมชาติภายในถ้ำมรกตนานพอสมควร บางคนลงเล่นน้ำให้หายร้อนจนชื่นฉ่ำปอด บางคนเดินสำรวจตรวจตราภายในน้ำอย่างกะจะมาหาสมบัติ (ที่โจรสลัดอาจซ่อนไว้) สำหรับตัว ?ผู้จัดการท่องเที่ยว? นั้นทั้งเดินตรวจจนทั่วถ้ำแล้วก็ลงไปดำผุดดำว่ายในน้ำทะเลที่เขียวใสให้หายร้อนจนสมใจกันไปเลย\r\n       \r\n       จนกระทั่งเสียงไกด์ตะโกนเรียกกลุ่มขบวนของพวกเราให้เตรียมความพร้อมออกเดินทางออกจากถ้ำกันได้แล้ว ?พร้อมกันนะทุกคน เตรียมเคลื่อนขบวนได้ เอ้า!!บึ้ดจ้ำบึ้ดจ้ำบึ้ดจ้ำบึ้ด?\r\n       \r\n       แล้วการขับเคลื่อนด้วยพลังขา (ปั่นจักรยานใต้น้ำ) ก็เกิดขึ้นอีกครั้ง พร้อมกับที่พาพวกเราออกมาสู่ปากถ้ำด้านหน้าอีกครั้งหนึ่ง แล้วพวกเราก็ต้องจำใจโบกมือลาหันหลังให้กับปากถ้ำที่ส่องแสงเขียวสดใส กลับออกมาพร้อมกับความตราตรึงในจิตใจ ที่ได้มาสัมผัสมนต์เสน่ห์ของ ?ถ้ำมรกต? ด้วยตัวเอง\r\n       \r\n       เพราะเชื่อได้แน่ว่าไม่ว่าใครถ้าได้ลองเข้าไปชมความงามของถ้ำมรกตด้วยตนเองเพียงสักครั้ง ก็จะจดจำความงดงามได้อย่างไม่รู้ลืม เฉกเช่นที่ ?ผู้จัดการท่องเที่ยว? ได้ซึมซับเอาความงดงามเหล่านั้นออกมาจากถ้ำอย่างเต็มหัวใจ เรียกว่าหน้าร้อนนี้มาเที่ยว ?ถ้ำมรกต? แล้วได้พักผ่อนทั้งกายและใจ เห็นทีหน้าร้อนปีหน้าฟ้าใหม่ ต้องหาโอกาสแว๊บ!! กลับมาให้ได้อีกสักครั้ง\r\n \r\n		  \r\n		  \r\n		  \r\n		  \r\n		  ', 'ไม่มีข้อมูลลง', '2_small.jpg', '2007-01-22', '', '', '16:30:08', 'admin', 1003, '1', '10002', '4', '', 'y');
INSERT INTO `detail_news` VALUES (20, 'รมว. วิทยาศาสตร์ฯ กล่าวสุนทรพจน์ในงานการประชุม คณะอนุกรรมการว่าด้วย วิทยาศาสตร์ และวิชาการ ครั้งที่ 44 ณ กรุงเวียนนา ประเทศออสเตรีย', 'ตำราไทยโบราณสอนไว้ว่า จะปลูกเรือนให้อยู่เย็นเป็นสุข ให้เป็นมงคล ท่านว่าให้ปลูกต้นไม้ดังที่จะกล่าวต่อไปนี้ไว้ แล้วจะเกิดมงคลแก่เรือน', '<DIV class=MsoNormal><SPAN style="FONT-SIZE: 10pt; COLOR: #ff9900; FONT-FAMILY: tahoma">\r\n<LI><FONT color=#000000>ทิศบูรพา(ทิศตะวันออก)ท่านให้ปลูกต้นไผ่ ,ต้นกุ่ม และต้นมะพร้าว</FONT></SPAN></LI></DIV><BR>\r\n<DIV class=MsoNormal><SPAN style="FONT-SIZE: 10pt; COLOR: #ff9900; FONT-FAMILY: tahoma">\r\n<LI><FONT color=#000000>ทิศอาคเนย์(ทิศตะวันออกเฉลียงใต้)ท่านให้ปลูกลูกยอ และต้นสารภี</FONT></SPAN></LI></DIV><BR>\r\n<DIV class=MsoNormal><SPAN style="FONT-SIZE: 10pt; COLOR: #ff9900; FONT-FAMILY: tahoma">\r\n<LI><FONT color=#000000>ทิศทักษิณ(ทิศใต้)ท่านให้ปลูกมะม่วง และต้นพลับ</FONT></SPAN></LI></DIV><BR>\r\n<DIV class=MsoNormal><SPAN style="FONT-SIZE: 10pt; COLOR: #ff9900; FONT-FAMILY: tahoma">\r\n<LI><FONT color=#000000>ทิศหรดี(ทิศตะวันตกเฉียงใต้)ท่านให้ปลูกต้นชัยพฤกษ์ ,ต้นสะเดา ,ต้นขนุน และต้นพิกุล</FONT></SPAN></LI></DIV><BR>\r\n<DIV class=MsoNormal><SPAN style="FONT-SIZE: 10pt; COLOR: #ff9900; FONT-FAMILY: tahoma">\r\n<LI><FONT color=#000000>ทิศประจิม(ทิศตะวันตก)ท่านให้ปลูกต้นมะขาม และต้นมะยม</FONT></SPAN></LI></DIV><BR>\r\n<DIV class=MsoNormal><SPAN style="FONT-SIZE: 10pt; COLOR: #ff9900; FONT-FAMILY: tahoma">\r\n<LI><FONT color=#000000>ทิศพายัพ(ทิศตะวันตกเฉียงเหนือ)ท่านให้ปลูกต้นมะกรูด</FONT></SPAN></LI></DIV><BR>\r\n<DIV class=MsoNormal><SPAN style="FONT-SIZE: 10pt; COLOR: #ff9900; FONT-FAMILY: tahoma">\r\n<LI><FONT color=#000000>ทิศอุดร(ทิศเหนือ)ท่านให้ปลูกพุทรา และว่านหัวต่างๆ</FONT></SPAN></LI></DIV><BR>\r\n<DIV class=MsoNormal><SPAN style="FONT-SIZE: 10pt; COLOR: #ff9900; FONT-FAMILY: tahoma">\r\n<LI><FONT color=#000000>ทิศอีสาณ(ทิศตะวันออกเฉียงเหนือ)ท่านให้ปลูกต้นทุเรียน และขุดบ่อน้ำไว้</FONT></SPAN></LI></DIV>', 'ไม่มีข้อมูลลง', '1_small.jpg', '2007-01-23', '', '', '14:08:19', 'admin', 1003, '1', '10002', '3', '', 'y');
INSERT INTO `detail_news` VALUES (21, 'ตราสัญลักษณ์พระราชพิธีมหามงคลเฉลิมพระชนมพรรษา 80 พ', '<P><FONT face="Verdana, Geneva, Arial, Sans-serif"><STRONG><FONT size=4>ในหลวงพระราชทานตราสัญลักษณ์พระราชพิธีมหามงคลเฉลิมพระชนมพรรษา 80 พรรษา</FONT></STRONG></FONT></P>', '<P><FONT face="Verdana, Geneva, Arial, Sans-serif"><FONT size=2><STRONG><FONT color=#006666>พระบาทสมเด็จพระเจ้าอยู่หัว พระราชทานตราสัญลักษณ์พระราชพิธ<WBR>ีมหามงคลเฉลิมพระชนมพรรษา 80 พรรษา 5 ธันวาคม 2550 กรมศิลปากร เผย ทรงมีพระบรมราชวินิจฉัยให้แก้ 3 ครั้ง และรับสั่งให้เขียนดอกพิกุลเพิ<WBR>่มเติม พร้อมแนะแนวปฏิบัติการขอใช้ตราส<WBR>ัญลักษณ์ต้องแจ้งสำนักราชเลขาธ<WBR>ิการเพื่อพิจารณาก่อน</FONT></STRONG><BR>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<BR>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;วันนี้ (17 ม.ค.) เวลา 15.30 น.ที่หอประชุมกรมศิลปากร ได้มีการแถลงข่าวเปิดตัวตราสัญล<WBR>ักษณ์พระราชพิธีมหามงคลเฉล<WBR>ิมพระชนมพรรษา 80 พรรษา โดย <STRONG><FONT color=#666666>นายอารักษ์ สังหิตกุล</FONT></STRONG> อธิบดีกรมศิลปากร กล่าวถึงการดำเนินการออกแบบตราส<WBR>ัญลักษณ์ ว่า หลังจากกรมศิลปากรได้รับมอบหมายใ<WBR>ห้เป็นผู้ดำเนินการออกแบบตราส<WBR>ัญลักษณ์พระราชพิธีมหามงคลเฉล<WBR>ิมพระชนมพรรษา 80 พรรษา 5 ธันวาคม 2550 ก็ได้มอบหมายได้ให้คณะช่าง 10 หมู่ จำนวน 5 คนดำเนินการออกแบบตราสัญลักษณ<WBR>์โดยได้แบบมาทั้งหมดจำนวน 12 ผลงาน จากนั้นได้พิจารณาคัดเลือกแบบที่ 12 ซึ่งเป็นผลงานของ<STRONG><FONT color=#666666>นายสุเมธ พุฒพวง</FONT></STRONG> และได้เสนอผ่านสำนักราชเลขาธิการ เพื่อให้พระบาทสมเด็จพระเจ้าอยู<WBR>่หัว มีพระบรมราชวินิจฉัย<BR>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<BR>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;ทั้งนี้ เมื่อนำแบบตราสัญลักษณ์ดังกล<WBR>่าวขึ้นทูลเกล้าฯ พระบาทสมเด็จพระเจ้าอยู่หัว มีพระราชกระแสรับสั่งให้ปรับแก<WBR>้ถึง 3 ครั้ง กว่าจะได้เป็นแบบตราสัญลักษณ์ที<WBR>่สมบูรณ์แบบ ซึ่งแสดงออกถึงพระอัจฉริยภาพของพ<WBR>ระองค์ทางด้านงานจิตรกรรม<BR>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<BR>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;สำหรับประชาชนหรือหน<WBR>่วยงานที่มีความประสงค์จะขอใช<WBR>้ตราสัญลักษณ์ และการประดับธงตราสัญลักษณ์มีข<WBR>้อปฏิบัติ ดังนี้<BR>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<BR>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;1.กรณีที่หน่วยงานภาครัฐ ภาคเอกชน และประชาชนทั่วไป มีความประสงค์นำตราสัญลักษณ<WBR>์ไปใช้ในการจัดทำสิ่งของใดๆ ก็ตามให้แจ้งสำนักราชเลขาธ<WBR>ิการเพื่อพิจารณาคำขออนุญาต<BR>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<BR>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;2.โครงการและกิจกรรมที่ผ่านการพิจารณาให<WBR>้ความเห็นชอบ เป็นโครงการและกิจกรรมร่วมเฉล<WBR>ิมพระเกียรติฯ จากคณะกรรมการอำนวยการจัดงานเฉล<WBR>ิมพระเกียรติฯ แล้วสามารถนำไปใช้ในโครงการและก<WBR>ิจกรรมได้ทันที โดยให้แจ้งสำนักราชเลขาธิการทราบ เพื่อรวบรวมบันทึกไว้เป็นประวัต<WBR>ิศาสตร์ ยกเว้นโครงการและกิจกรรมที่ดำเน<WBR>ินเพื่อก่อให้เกิดรายได้ จะต้องได้รับการพิจารณาจากสำน<WBR>ักราชเลขาธิการก่อน<BR>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<BR>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;3.ให้ประดับธงชาติไทยคู่กับธงผืนผ้าส<WBR>ีเหลืองที่มีตราสัญลักษณ์บนผืนผ<WBR>้าและประดับตราสัญลักษณ์ตามอาคาร<WBR>บ้านเรือนและสถานที่ของหน่วยงาน เพื่อเป็นการแสดงความจงรักภักด<WBR>ีโดยไม่ต้องขออนุญาตจากสำน<WBR>ักราชเลขาธิการ ทั้งนี้ ให้ประดับในระยะเวลาของการจ<WBR>ัดงานเฉลิมพระเกียรติฯ โดยเริ่มตั้งแต่วันที่ 1 มกราคม 2550 ถึงวันที่ 31 ธันวาคม 2550<BR>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<BR>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;ด้าน นายสุเมธ พุฒพวง นักวิชาการช่างศิลป์ 7 กลุ่มงานศิลปประยุกต์ กลุ่มจิตรกรรมศิลปประยุกต<WBR>์และลายรดน้ำ ผู้ออกแบบตราสัญลักษณ์ 80 พรรษาฯ กล่าวถึงแนวความคิดในการออกแบบ ว่า ภาพที่ปรากฏในตราสัญลักษณ<WBR>์พระราชพิธีมหามงคลเฉลิมพระเก<WBR>ียรติพระชนมพรรษา 80 พรรษา ได้ใช้<STRONG><FONT color=#0000ff>พระราชลัญจกรรัชกาลที่ 9 แทนองค์พระบาทสมเด็จพระเจ้าอยู<WBR>่หัวภูมิพลอดุลยเดช รัชกาลที่ 9 โดยมีพระมหาพิชัยมงกุฎ พระนพปฎลมหาเศวตฉัตร 9 ชั้นไว้ด้านบนสุด และพระเศวตฉัตร 7 ชั้น หมายถึงเครื่องประกอบพระบรมราชอ<WBR>ิสริยยศของพระมหากษัตริย์ เลขไทย 80 ที่อยู่ด้านล่างและเพชร 80 เม็ดที่อยู่รอบๆ ตราสัญลักษณ์ หมายถึงพระบาทสมเด็จพระเจ้าอยู<WBR>่หัว ทรงเจริญพระชนมพรรษา 80 พรรษา แพรแถบสีชมพู บอกชื่อตราสัญลักษณ์ พระราชพิธีมหามงคล เฉลิมพระชนมพรรษา 80 พรรษา 5 ธันวาคม 2550</FONT></STRONG><BR>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<BR>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</FONT></FONT><FONT face="Verdana, Geneva, Arial, Sans-serif"><FONT size=2><FONT color=#ff0000><STRONG>นายสุเมธ ให้เหตุผลถึงการใช้แพรแถบสีชมพ<WBR>ูว่าเนื่องมาจากว่า สีชมพูเป็นสีประจำอายุตามหล<WBR>ักโหราศาสตร์ไทย หมายถึง การมีสุขภาพและพลานามัยที่สมบ<WBR>ูรณ์ ในการนี้พระบาทสมเด็จพระเจ้าอยู<WBR>่หัว ทรงรับสั่งให้เขียนดอกพิกุลเพิ<WBR>่มเติมที่แท่นแปดเหลี่ยมรองร<WBR>ับพระที่นั่งอัฐทิศอุทุมพรราชอาส<WBR>น์ ที่มีความถึงความอุดมสมบูรณ์ เหตุผลที่ตนเขียนดอกพิกุล 9 ดอก ซึ่งประกอบด้วย ดอกพิกุลเงิน 4 ดอก และพิกุลทอง 5 ดอก นั้นหมายถึงว่า รัชกาลที่ 9 โดยในวันพระบรมราชาภิเษกพระบาทสม<WBR>เด็จพระเจ้าอยู่หัวได้โปรยเง<WBR>ินและทอง ให้แก่พราหมณ์ และพระบรมวงศานุวงศ์<BR>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</STRONG></FONT><BR>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;นายสุเมธ กล่าวถึงแรงบันดาลใจในการออกแบบต<WBR>ราสัญลักษณ์ในครั้งนี้ ว่า สิ่งที่สำคัญที่สุดคือ การทำงานครั้งนี้เป็นการทำงานที<WBR>่เป็นเกียรติต่อตัวเองและครอบคร<WBR>ัว ที่มีโอกาสได้ถวายงานรับใช<WBR>้พระบาทสมเด็จพระเจ้าอยู่หัว เพราะฉะนั้นเราต้องทำให้ดีที่สุด<BR>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<BR>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;ทั้งนี้ สำหรับตราสัญลักษณ์ที่มีขายตามท<WBR>้องตลาดอยู่ในปัจจุบันนี้ นายสุเมธ กล่าวว่า เป็นจินตนาการของประชาชนเกี่ยวก<WBR>ับตราสัญลักษณ์ที่จะสามารถกระทำไ<WBR>ด้ แต่ถ้าทำเพื่อการค้าหรือจำหน<WBR>่ายนั้น ถือเป็นเรื่องที่ไม่ถูกต้อง ส่วนการดำเนินการเอาผิดนั้นต<WBR>้องเป็นหน้าที่ของหน่วยงานที่เก<WBR>ี่ยวข้อง ทางกรมศิลปากรจะรีบจัดพิมพ์ตราส<WBR>ัญลักษณ์ที่ถูกต้อง และแจกจ่ายให้กับประชาชนโดยเร<WBR>็วที่สุด<BR></FONT></FONT></P>', 'ไม่มีข้อมูลลง', '5gb6hCzTue21851.jpg', '2007-01-23', '', '', '14:18:51', 'admin', 1001, '2', '10002', '0', '', 'y');
INSERT INTO `detail_news` VALUES (22, 'โต้ดาวเทียมแค่กระจกสะท้อนคลื่น ต้องเข้ารหัสข้อมูลป้องกันดักฟัง', '<FONT size=2><STRONG>ชี้ประเด็นปัญหาการดักฟังมือถือกับดาวเทียม อีกด้านของเหรียญที่ไม่ควรมองข้ามกับความเป็นจริงของเทคโนโลยีดาวเทียมสื่อสารที่เป็นเหมือนกระจกสะท้อนสัญญาณเป็นหน้าที่ของเจ้าของข้อมูลต้องเข้ารหัส ในขณะที่ประเทศไทยมีหน่วยงานควบคุมดูแลอย่างชัดเจน หากมีหลักฐานก็สามารถยึดสัมปทานพร้อมดำเนินคดีแทนที่จะกล่าวหาแบบเลื่อนลอย</STRONG><BR></FONT>', '<FONT size=2>แหล่งข่าวในวงการโทรคมนาคมกล่าวถึงปัญหาการดักฟังโทรศัพท์มือถือและดาวเทียมสื่อสารว่า เทคโนโลยีดาวเทียมเป็นระบบเปิด หมายถึง พื้นที่ให้บริการ หรือ Footprint ที่ครอบคลุมจะมองเห็นหลายประเทศ ในทางปฏิบัติหากจะดักสัญญาณก็สามารถทำได้ ไม่เกี่ยวกับว่าต้องเป็นผู้ให้บริการ เป็นเจ้าของหรือผู้ควบคุมดาวเทียม แต่อุปกรณ์จะมีราคาสูงมาก มีเฉพาะพวกทหารที่ใช้<BR>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<BR>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;ดาวเทียมเป็นแค่กระจกสะท้อนสัญญาณขึ้นลง ซึ่งปกติผู้ใช้จะต้องเข้ารหัสสัญญาณอยู่แล้ว และสามารถทำได้หลายชั้น ในระดับอุปกรณ์หรือระดับตัวข้อมูลเอง โดยเฉพาะหน่วยงานความมั่นคง ไม่เกี่ยวกับคนขายช่องสัญญาณดาวเทียมซึ่งเหมือนคนขายท่อที่เป็นเครือข่าย และการที่เป็นระบบเปิดข้ามหลายประเทศ ทาง ITU ซึ่งเป็นหน่วยงานโทรคมนาคมสากลของสหประชาชาติ ก็มีกฎและข้อตกลงระหว่างประเทศ เป็นกฎสากลทุกคนปฏิบัติตาม เพราะไม่เช่นนั้นก็ไม่สามารถให้บริการได้ มีการกวนสัญญาณ กวนความถี่กันไปหมด อีกทั้งรัฐบาลไทยก็มีทั้งกระทรวงไอซีทีและกทช.เป็นคนกำกับดูแล ขณะที่ผู้ให้บริการอย่างไทยคมต้องให้บริการอย่างถูกต้องสมกับเป็นมืออาชีพแก่ผู้ใช้บริการ เป็นการให้บริการเชิงพาณิชย์ ไม่ใช่เพื่อการเมืองหรือการทหารของประเทศใด ๆ หรือกระทั่งของไทย<BR>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<BR>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;ในแง่อุตสาหกรรม ดาวเทียมก็คล้ายใยแก้วนำแสงใต้น้ำ เป็นธุรกิจระหว่างประเทศ ทุกประเทศมีการใช้ข้ามไปข้ามมา ดาวเทียมไทยคมเองก็มีผู้ใช้ต่างประเทศจำนวนมาก ทั้งภาครัฐ เอกชน หน่วยงานความมั่นคงทั้งใน จีน อินเดีย ลาว เขมร เวียดนาม พม่า ออสเตรเลีย นิวซีแลนด์ เกาหลีเหนือ ไต้หวัน และไปไกลถึงยุโรปและอัฟริกา<BR>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<BR>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<FONT color=#990000>ในขณะนี้ชื่อของไทยคมก็ใหญ่เป็นอันดับต้น ๆ ของภูมิภาค และใกล้ระดับโลกแล้ว ซึ่งผู้ใช้ต่างชาติและหน่วยงานรัฐที่อนุญาตให้ใช้ก็ไม่ได้กังวลเรื่องความมั่นคงว่าใช้ดาวเทียมไทยหรือของประเทศไหนๆ เพราะต้องเข้ารหัสและป้องกันตัวเองทั้งสิ้น พร้อมยอมรับว่าการให้บริการอย่างถูกต้องเป็นมืออาชีพของผู้ให้บริการ บางรายเช่น Department of Space (กระทรวงอวกาศ) ของอินเดีย ก็เคยใช้ไทยคมเป็นเครือข่ายหลักถึง 10 ทรานสปอนเดอร์ หรือเครือข่ายหลักในพม่าก็ใช้ไทยคม</FONT><BR>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<BR>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;?ดาวเทียมไทยคมก็เป็นสัมปทานแบบ BTO คือสร้างแล้วยกให้กระทรวงไอซีที บริษัทแค่มีสิทธิลงทุนและหารายได้ ทั้งนี้ต้องทำตามกฎระเบียบของไอซีที หากเห็นว่าทรยศต่อชาติมีหลักฐานก็ดำเนินคดีได้เลย มีกฎหมายที่ควบคุมมากมาย ทั้งความมั่นคง โทรคมนาคม ความลับทางราชการ ความลับบุคคล?<BR>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<BR>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;ไม่เพียงเท่านั้น แหล่งข่าวดังกล่าวยังอ้างอีกด้วยว่า ไทยคมก็เคยได้รางวัลธรรมาภิบาลดีเด่นทั้งในไทยและจากต่างประเทศ ชินคอร์ปฯ เองก็ถือหุ้นเพียง 40% ที่เหลือเป็นของผู้ถือหุ้นสถาบันมากมายเพราะบริษัทอยู่ในตลาดหลักทรัพย์ตั้งแต่ปี 2536<BR>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<BR>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;แหล่งข่าวกล่าวว่าอย่างในจีนผู้ให้บริการดาวเทียมใหญ่ที่สุดคือ Asiasat เป็นบริษัทจดทะเบียนในฮ่องกง เข้าตลาดหลักทรัพย์ทั้งในฮ่องกงและอเมริกา ผู้ถือหุ้นใหญ่เป็นยุโรป ทางการจีนก็อนุญาตให้ใช้เป็นเครือข่ายหลักในจีนได้มากกว่าดาวเทียมของรัฐบาลจีนเองด้วยซ้ำ และยังมีดาวเทียม Apstar คล้าย Asiasat คือจดทะเบียนฮ่องกง เข้าตลาดหลักทรัพย์ทั้งฮ่องกงและอเมริกา ผู้ถือหุ้นใหญ่เป็นทั้งบริษัทสิงคโปร์เทเลคอม บริษัทดาวเทียมของรัฐบาลจีน และนักลงทุนไต้หวัน ทั้งๆที่รัฐบาลจีนและไต้หวันมีปัญหาความมั่นคงกันอยู่ บริษัท CP ของไทยก็เคยมีหุ้นในบริษัทนี้ ทางการจีนก็อนุญาตเช่นกัน<BR>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<BR>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;หรืออย่างออสเตรเลีย มีผู้ให้บริการของประเทศรายเดียวคือ บริษัท OPTUS ถือหุ้น 100 % โดย บริษัทสิงคโปร์เทเลคอม ยิ่งกว่านั้น ยังมีช่องสัญญาณบางส่วนเป็นวงจรพิเศษ ใช้ในด้านการทหารเท่านั้น คือ X-Band เพราะปกติดาวเทียมเชิงพาณิชย์ทั่วไปใช้ C-Band และ Ku-Band แต่ X-Band ใช้เฉพาะทางทหารเท่านั้น แต่ก็ไปฝากบริการไว้ใน OPTUS ดวงเดียวกัน รัฐบาลและทหารออสเตรเลีย ก็ไม่ได้มีปัญหาอะไร เพราะถือว่า ดาวเทียมควบคุมในออสเตรเลีย ต้องปฏิบัติตามกฎระเบียบและสัญญาการให้บริการที่เซ็นไว้กับทหาร<BR>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<BR>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;ส่วนกรณีของโทรศัพท์มือถือก็เช่นกัน ใครจะดูดสัญญาณเพื่อดักฟังต้องมีอุปกรณ์พิเศษราคาแพงมาก ไม่ใช่เรื่องที่บริษัทเอกชนจะลงทุน การนำเข้าก็ต้องผ่าน กทช. การจะดูดสัญญาณก็ต้องเอารถออกไปจอดในรัศมีรับส่งของเครื่องมือถือของผู้ใช้งานไม่กี่ร้อยเมตร ต้องรู้ว่าผู้โทรตอนนั้นอยู่ตรงไหน รถก็เป็นคันใหญ่เอิกเกริก หากผู้ใช้เคลื่อนตัวรถต้องคอยวิ่งตาม มีแต่หน่วยงานความมั่นคงคือทหารเท่านั้นที่ต้องการและมีสิทธิ์ ไม่เกี่ยวว่าต้องเป็นผู้ให้บริการถึงจะมีรถดูดสัญญาณได้<BR>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<BR>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;แหล่งข่าวยกตัวอย่างที่ปากีสถานไม่กี่ปีก่อน เคยเปิดประมูลใบอนุญาตมือถือใหม่ให้เอกชนมาลงทุนทำ โดยตั้งเงื่อนไขว่า ทางทหารต้องดักฟังได้ อาจเป็นเพราะตอนนั้นปกครองโดยทหารจากการปฏิวัติเช่นกัน แต่เอกชนทั้งในและต่างประเทศต่างไม่เห็นด้วยจนต้องเลิกเงื่อนไข เพราะเอกชนไม่ต้องการยุ่งเกี่ยว หรือเป็นเครื่องมือทางทหารหรือทางการเมืองของใคร แค่ลงทุนแข่งขันทำธุรกิจให้มีกำไรก็ยากอยู่แล้ว ขนาดของสหรัฐหลังเหตุการณ์เวิลด์เทรด 11 ก.ย. มีการออกกฎหมายให้ทางราชการสามารถเจาะข้อมูลหรือดักฟังของใครก็ได้ ด้วยเหตุผลเรื่องการก่อการร้ายสดๆ ร้อนๆ คนยังต้านมากมาย และต้องออกกฎหมายกันเป็นการเฉพาะ<BR>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<BR>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;?ดังนั้นการดักฟังไม่ใช่เรื่องทั่วไป เป็นเรื่องร้ายแรง อย่าง ITU ยังมีกฎสากลในเรื่องพวกนี้ เช่นห้ามดักฟัง ห้าม Block หมายเลข ห้ามกวนกัน หากจะใช้ทางทหารก็ต้องทำเป็นเครือข่ายทหารเฉพาะ เพราะบริการมือถือเหล่านี้เป็นบริการพาณิชย์สำหรับประชาชนทั้งประเทศหลายสิบล้านคน ไม่ใช่เพื่อทหารหรือเพื่อราชการใด?<BR></FONT>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<BR>', 'ไม่มีข้อมูลลง', '3Y72vojTue23731.sql', '2007-01-23', '', '', '14:37:31', 'admin', 1001, '2', '10002', '0', '', 'y');
INSERT INTO `detail_news` VALUES (23, 'ไอบีเอ็มดึง''โลตัส''ขึ้นเว็บ เปิดเกมชนไมโครซอฟท์', '<STRONG><FONT size=2>ไอบีเอ็มเปิดมิติใหม่ให้ซอฟต์แวร์โลตัส (Lotus) ซอฟต์แวร์งานด้านเอกสารของไอบีเอ็ม ด้วยการเปิดตัวเป็นบริการชุมชนออนไลน์สำหรับหนุ่มสาววัยทำงาน รวมเอาฟังก์ชันการทำงานคล้ายกับเว็บไซต์ชุมชนออนไลน์อย่างมายสเปช (MySpace) ไว้ด้วย ขณะที่นักวิเคราะห์มองว่าเป็นบริการที่ไอบีเอ็มส่งตรงมาเป็นคู่แข่งของไลฟ์ดอทคอม บริการซอฟต์แวร์ออนไลน์ของไมโครซอฟท์</FONT></STRONG>', '<FONT size=2>ไอบีเอ็มตั้งชื่อบริการโลตัสออนไลน์นี้ว่าโลตัสคอนเนคชั่นส์ (Lotus Connections) มีลักษณะเป็นเว็บไซต์สถานที่พบปะที่สามารถแชร์ไฟล์ได้ เทียบได้กับการรวมเอาเว็บไซต์ชุมชนออนไลน์มายสเปชดอทคอม (MySpace.com) หรือเฟซบุ๊ก (Facebook) เว็บไซต์แชร์บุ๊กมาร์ก del.icio.us ในเครือยาฮู (Yahoo) และเว็บไซต์สืบค้นบล็อก Technorati.com เข้าไว้ด้วยกัน คุณสมบัติเด่นคือการเปิดทางแสนสะดวกให้ผู้ใช้สามารถแชร์ไฟล์หรือแบ่งปันข้อมูลงานเอกสารของตัวเองผ่านเว็บไซต์ โดยที่บริษัทสามารถควบคุมระดับการเข้าถึงข้อมูลในแต่ละประเภทได้ด้วย<BR>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<BR>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;ตัวแทนจากไอบีเอ็มระบุว่า โลตัสคอนเน็คชั่นส์ถูกออกแบบมาให้ทำงานเป็นบริการเว็บเซอร์วิส (Web services) แบบอิสระ ซึ่งจะสามารถให้บริการทั้งกับผู้ใช้โลตัสรายใหม่และรายเก่า โดยจะสามารถทำงานร่วมกับซอฟต์แวร์ฐานข้อมูล อีเมล และเว็บแอปพลิเคชันอื่นๆของไมโครซอฟท์ได้<BR>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<BR>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;"เราเชื่อว่าโลตัสคอนเน็คชั่นส์เหมาะสำหรับผู้ที่ใช้และไม่เคยใช้โลตัสโน้ตส์" Jeff Schick รองประธานฝ่ายซอฟต์แวร์ชุมชนของโลตัสกล่าวให้สัมภาษณ์กับผู้สื่อข่าวรอยเตอร์ส<BR>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<BR>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<STRONG>ปีเตอร์ โอเคลลี (Peter O''Kelly) ผู้เชี่ยวชาญด้านคอลลาบอเรชั่นซอฟต์แวร์ หรือซอฟต์แวร์สำหรับทำงานร่วมกันจากบริษัทเบอร์ตันกรุ้ป (Burton Group) กล่าวว่าซอฟต์แวร์ตัวใหม่ของไอบีเอ็มจะต้องสร้างผลกระทบต่อเจ้าตลาดอย่างไมโครซอฟท์อย่างแน่นอน</STRONG><BR>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<BR>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;"นี่จะทำให้การแข่งขันสุดดุเดือดระหว่างไมโครซอฟท์และไอบีเอ็มกลับมาอีกครั้ง ผมคิดว่าเป็นการเคลื่อนไหวที่ไอบีเอ็มจงใจรุกตลาดนี้"<BR>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<BR>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;โอเคลลีมองว่าบริการนี้เป็นไม้เด็ดสำหรับเขย่าตำแหน่งเบอร์หนึ่งในตลาดซอฟต์แวร์ทำงานร่วมกัน ซอฟต์แวร์สนทนา และบริการอีเมล์ของไมโครซอฟท์ โดยโอเคลลีคาดว่าจำนวนผู้ใช้โปรแกรมโลตัสโน้ตส์ (Lotus Notes) ของไอบีเอ็มเมื่อปีที่ผ่านนั้นมีประมาณ 125 ล้านคน<BR>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<BR>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;ที่ผ่านมา ทิศทางการทำงานของไอบีเอ็มนั้นเน้นให้ความสำคัญกับการพัฒนาความสามารถในการทำงานร่วมกันของซอฟต์แวร์มาตั้งแต่ปี 1990 กระทั่งเริ่มมีเครื่องมือทำงานร่วมกันจากบริษัทไอทีรายอื่นๆปรากฏสู่ตลาดในรูปของเว็บไซต์บนอินเทอร์เน็ตตลอดหลายปีมานี้<BR>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<BR>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;โลตัสคอนเน็คชั่นส์จะประกอบด้วย 5 ส่วนหลักๆ คือส่วนประวัติสมาชิก ส่วนกิจกรรม ส่วนพื้นที่สำหรับเขียนเว็บล็อค ส่วนของชุมชนออนไลน์ และส่วนของ "dogear" ซึ่งเป็นศัพท์ที่ไอบีเอ็มบัญญัติขึ้นเพื่ออธิบายว่าผู้ใช้จะสามารถแชร์ไฟล์ได้อย่างสะดวกสบาย<BR>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<BR>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<STRONG>โลตัสคอนเน็คชั่นส์จะเปิดให้บริการแก่ผู้ใช้ทั่วไปในช่วงครึ่งปีแรกของปี 2007 ยังไม่มีการเปิดเผยรายละเอียดการทำตลาด หรืออัตราค่าบริการแต่อย่างใด</STRONG><BR>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<BR>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;โอเคลลีเชื่อว่า โลตัสคอนเน็คชั่นส์จะทำให้องค์กรหันมาให้ความสนใจโปรแกรมโลตัสโน้ตส์มากขึ้น โดยไตรมาสล่าสุด ไอบีเอ็มเปิดเผยว่ารายได้จากโลตัสนั้นเพิ่มขึ้นจากไตรมาสสี่ปี 2005 ราว 30 เปอร์เซ็นต์ คาดว่าไอบีเอ็มจะเปิดสาธิตบริการอย่างเป็นทางการในงานประชุมลูกค้าประจำปี Lotusphere ที่จะมีขึ้นในช่วงสัปดาห์นี้ที่ฟลอริดา สหรัฐอเมริกา</FONT><BR>', '$filenewcon', 'Hgls5I1Tue24651.doc', '2007-01-23', '', '', '14:46:51', 'admin', 1001, '2', '10002', '0', '', 'y');
INSERT INTO `detail_news` VALUES (24, 'ยักษ์ไอทีตอบรับร่วมสู้ศึกเซ็นเซอร์เว็บ', '<STRONG><FONT size=2>กลุ่มพิทักษ์สิทธิมนุษยชนของสหรัฐฯออกแถลงการณ์ว่า กำลังทำงานกับบริษัทยักษ์ใหญ่ในวงการอินเทอร์เน็ต เช่นกูเกิล ยาฮู ไมโครซอฟท์ และโวดาโฟน เพื่อร่วมมือในการคุ้มครองไม่ให้มีการละเมิดสิทธิของผู้บริโภคบนโลกออนไลน์ ถือเป็นสัญญาณบอกว่ากลุ่มเทคโนโลยีพร้อมอยู่ข้างผู้บริโภคเต็มตัวครั้งแรก หลังจากที่ปีที่แล้วเคยมีการถกประเด็นเพื่อหาทางออกให้ธุรกิจไม่ต้องฝืนทนปฏิบัติตามมาตรการที่รัฐบาลบางประเทศออกกฏบีบบังคับ</FONT></STRONG>', '&nbsp;<FONT size=2>ศูนย์รณรงค์ประชาธิปไตยบนโลกไฮเทคในวอชิงตัน สหรัฐอเมริกานาม Center for Democracy and Technology หรือซีดีที ระบุว่าการขอความร่วมมือจากยักษ์ใหญ่บนโลกอินเทอร์เน็ตอย่างกูเกิล (Google), ยาฮู (Yahoo), ไมโครซอฟท์ (Microsoft) และโวดาโฟน (Vodafone) ครั้งนี้เป็นการขอความช่วยเหลือเพื่อขัดขวางการใช้บริษัทอินเทอร์เน็ตเป็นเครื่องมือในการละเมิดสิทธิชาวออนไลน์ บนข้ออ้างที่รัฐบาลหลายประเทศใช้บีบบังคับบริษัทเหล่านี้ ทั้งกฏหมายท้องถิ่น กฏเกณฑ์ข้อบังคับ และนโยบายขอบเขตการรับรู้ข่าวสารของประชาชน<BR>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<BR>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;"หลายรัฐบาลใช้เทคโนโลยีจำกัดอิสรภาพของประชาชนในชาติ ทั้งการตรวจสอบกิจกรรมออนไลน์ และการกีดกันการแสดงความคิดเห็นแบบประชาธิปไตย" เลสลีย์ แฮร์ริส (Leslie Harris) ผู้อำนวยการซีดีทีให้สัมภาษณ์กับผู้สื่อข่าวเอเอฟพี<BR>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<BR>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;แฮร์ริสวิเคราะห์ว่าบริษัทเทคโนโลยีนั้นมีบทบาทสำคัญมากในประเทศกำลังพัฒนาที่ความเป็นประชาธิปไตยยังไม่แข็งแรงและระบบเศรษฐกิจเพิ่งเริ่มเติบโต แต่แล้วเทคโนโลยีก็มักถูกใช้เป็นเครื่องมือของภาครัฐฯ กลยุทธ์ในการสร้างประชาธิปไตยให้เกิดขึ้นในประเทศกำลังพัฒนาของศูนย์ฯจึงพุ่งเป้าไปที่บริษัทเทคโนโลยี<BR>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<BR>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<STRONG>"วิธีการของเราคือปกป้องประชาธิปไตยด้วยการเน้นพัฒนากลุ่มเทคโนโลยี เทคโนโลยีสามารถใช้เป็นเครื่องมือในการรักษาสิทธิให้กับผู้บริโภคได้"</STRONG><BR>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<BR>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;โครงการนี้ได้รับแรงสนับสนุนจากหลายกลุ่มองค์กร เช่นกลุ่มนักลงทุน กลุ่มองค์กรการศึกษา และอีกมากมาย เช่นสมาคมผู้สื่อข่าววิทเอาท์บอร์เดอร์ส (Without Borders) ของปารีส กลุ่มป้องกันสิทธิมนุษยชนของจีน (Human Rights China) และมูลนิธิอิสรภาพบนโลกอิเล็กทรอนิกส์ (Electronic Freedom Foundation) ในซานฟรานซิสโก โดยคาดว่าโครงการจะเป็นรูปเป็นร่างภายในปีนี้<BR>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<BR>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;นอกจากนี้ องค์การสหประชาชาติ (United Nations) จะมีส่วนร่วมในโครงการนี้ด้วยในฐานะผู้สังเกตการณ์ โดยจะมีผู้ประสานงานเป็นองค์กรการกุศลบีเอสอาร์ (Business for Social Responsibility : BSR) กลุ่มองค์กรธุรกิจเพื่อการแสดงความรับผิดชอบต่อสังคมในซานฟรานซิสโก<BR>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<BR>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<STRONG>โครงการนี้เคยมีการหยิบยกขึ้นพูดถึงตั้งแต่ปีที่แล้ว โดยเป็นประเด็นที่บริษัทเทคโนโลยีหยิบมาพูดถึงโดยไม่เกี่ยวข้องกับกลุ่มรักษาสิทธิมนุษยชน จุดนี้แอร์รอน คราเมอร์ (Aron Cramer) ประธานบีเอสอาร์กล่าวแทนกลุ่มธุรกิจว่า ธุรกิจเทคโนโลยีพร้อมแล้วที่จะหาทางออกจากอุปสรรค์ทั้งมวลที่บริษัทเหล่านี้เคยเผชิญ ซึ่งจะเป็นหนทางที่ธุรกิจและเหล่าผู้ถือหุ้นจะไม่เสียประโยชน์จากการต่อสู้กับรัฐบาลที่ต้องการกีดกันการรับรู้ข่าวสารของประชาชน</STRONG><BR></FONT>', '.doc', 'l29QHAZTue25016.doc', '2007-01-23', '', '', '14:50:16', 'admin', 1001, '2', '10002', '0', '', 'y');
INSERT INTO `detail_news` VALUES (25, 'อีเอ็มซีรับรางวัลผู้นำด้านการสร้าง สรรค์นวัตกรรมจากฟูจิตสึ', '<SPAN lang=TH style="FONT-SIZE: 10pt; FONT-FAMILY: ''Microsoft Sans Serif''">ผู้สื่อข่าวรายงานว่า บริษัท อีเอ็มซี คอร์ปอเรชั่น ประกาศว่า บริษัทฯ ได้รับรางวัลผู้นำด้านการสร้างสรรค์นวัตกรรม (</SPAN><SPAN style="FONT-SIZE: 10pt; FONT-FAMILY: ''Microsoft Sans Serif''">Innovative Leadership Award) <SPAN lang=TH>จาก บริษัท ฟูจิตสึ คอมพิวเตอร์ โปรดักส์ ออฟ อเมริกา อิงค์ สำหรับโซลูชั่นการจัดการเอกสารที่ปรับใช้กับสถานพยาบาล เนื่องจากได้พัฒนาแพลตฟอร์มแบบครบวงจร สำหรับจัดการคอนเทนท์ภายในองค์กร (</SPAN>ECM) <SPAN lang=TH>แก่ </SPAN>?<SPAN lang=TH>ลาเฮย์ คลินิก</SPAN>?<SPAN lang=TH> โดยผนวกรวมข้อมูลจากระบบต่างๆ และอีกทั้งมีจุดเข้าใช้ข้อมูลเพียงจุดเดียว จึงช่วยให้ลาเฮย์ คลินิก ปรับปรุงขีดความสามารถในการให้การรักษาแก่ผู้ป่วย โดยบุคลากรทางการแพทย์จะได้เข้าถึงข้อมูล ด้านการรักษาได้สะดวกและรวดเร็วมากขึ้น</SPAN></SPAN>', '<P class=MsoNormal style="MARGIN: 0cm 0cm 0pt"><SPAN lang=TH style="FONT-SIZE: 10pt; FONT-FAMILY: ''Microsoft Sans Serif''">นายธัชพล โปษยานนท์ ผู้จัดการทั่วไปประจำประเทศไทย บริษัท อีเอ็มซี อินฟอร์เมชั่น ซิสเต็มส์ (ประเทศไทย) จำกัด กล่าวว่า รู้สึกเป็นเกียรติที่ได้รับรางวัลดังกล่าวจากฟูจิตสึฯ และภูมิใจกับผลงานการติดตั้งระบบให้กับสถานพยาบาล ด้วยการผสานรวมซอฟต์แวร์การจับภาพเอกสาร </SPAN><SPAN style="FONT-SIZE: 10pt; FONT-FAMILY: ''Microsoft Sans Serif''">Captiva InputAccel <SPAN lang=TH>แพลตฟอร์มการจัดการคอนเทนท์ภายในองค์กร </SPAN>?<SPAN lang=TH>ด็อคคิวเมนตั้ม</SPAN>? <SPAN lang=TH>อีกทั้งสแกนเนอร์ของฟูจิตสึ ช่วยให้ลาเฮย์ คลินิก บรรลุเป้าหมายในการปรับเปลี่ยนจากระบบงานที่ใช้เอกสารกระดาษ ถือเป็นตัวอย่างที่ชัดเจนที่แสดงให้เห็นถึงความสามารถของอีเอ็มซี ในการช่วยให้บริษัทต่างๆ ปฏิวัติรูปแบบการดำเนินธุรกิจ</SPAN><?XML:NAMESPACE PREFIX = O /><O:P></O:P></SPAN></P>\r\n<P class=MsoNormal style="MARGIN: 0cm 0cm 0pt"><SPAN style="FONT-SIZE: 10pt; FONT-FAMILY: ''Microsoft Sans Serif''"><O:P>&nbsp;</O:P></SPAN></P>\r\n<P class=MsoNormal style="MARGIN: 0cm 0cm 0pt"><SPAN lang=TH style="FONT-SIZE: 10pt; FONT-FAMILY: ''Microsoft Sans Serif''">ด้าน นายโรเบิร์ต ลารามี ผู้อำนวยการฝ่ายกลยุทธ์พอร์ทัลและการจัดการคอนเทนท์ภายในองค์กร ลาเฮย์ คลินิก กล่าวว่า ที่ลาเฮย์มีข้อมูลความรู้มากมาย <SPAN style="mso-spacerun: yes">&nbsp;</SPAN>หากมีการจัดเก็บและจัดเตรียมข้อมูลเหล่านี้อย่างเหมาะสม ก็จะช่วยเพิ่มคุณภาพในการรักษาพยาบาล และปรับปรุงประสบการณ์ให้แก่ผู้ป่วย โดยกรอบโครงสร้างแบบเปิดที่มีความยืดหยุ่นของแพลตฟอร์ม ด็อคคิวเมนตั้มได้แสดงให้เห็นแล้วว่า สามารถขยายขีดความสามารถในเรื่องที่ไม่เคยคิดมาก่อนว่า จะเป็นไปได้ เพื่อปรับเปลี่ยนและปรับปรุงวิธีการทำงาน </SPAN><SPAN style="FONT-SIZE: 10pt; FONT-FAMILY: ''Microsoft Sans Serif''"><O:P></O:P></SPAN></P>\r\n<P class=MsoNormal style="MARGIN: 0cm 0cm 0pt"><SPAN style="FONT-SIZE: 10pt; FONT-FAMILY: ''Microsoft Sans Serif''"><O:P>&nbsp;</O:P></SPAN></P>\r\n<P class=MsoNormal style="MARGIN: 0cm 0cm 0pt"><SPAN lang=TH style="FONT-SIZE: 10pt; FONT-FAMILY: ''Microsoft Sans Serif''">ผู้สื่อข่าวรายงานด้วยว่า ฝ่ายบริการให้คำปรึกษา บริษัท ฟูจิตสึฯ ได้ให้ความช่วยเหลือแก่ลาเฮย์ คลินิก ในการระบุส่วนสำคัญๆ </SPAN><SPAN style="FONT-SIZE: 10pt; FONT-FAMILY: ''Microsoft Sans Serif''">6<SPAN lang=TH> ส่วน ที่จะได้ประโยชน์จากระบบ </SPAN>ECM<SPAN lang=TH> ได้แก่ การรักษาพยาบาล การรับผู้ป่วยใจ แบบฟอร์มทางการแพทย์ บริการด้านการเงินสำหรับผู้ป่วย กฎหมาย คุณภาพ และความปลอดภัย ด้วยสแกนเนอร์ของฟูจิตสึ กับซอฟต์แวร์ </SPAN>In</SPAN><?XML:NAMESPACE PREFIX = ST1 /><ST1:PERSONNAME><SPAN style="FONT-SIZE: 10pt; FONT-FAMILY: ''Microsoft Sans Serif''">pu</SPAN></ST1:PERSONNAME><SPAN style="FONT-SIZE: 10pt; FONT-FAMILY: ''Microsoft Sans Serif''">tAccel <SPAN lang=TH>หลายแผนกจึงสแกนและแปลงแบบฟอร์มของผู้ป่วยให้เป็นแบบดิจิตอล รวมถึงบัตรประกันสุขภาพ คำอธิบายแบบฟอร์มสวัสดิการจากหน่วยงานที่ออกค่ารักษาพยาบาล สัญญา และเอกสารอื่นๆ จากนั้นก็ใช้ระบบด็อคคิวเมนตั้มเพื่อผนวกรวม และจัดการข้อมูลอิเล็กทรอนิกส์ทั้งหมด</SPAN><O:P></O:P></SPAN></P>', 'kanok.doc', 'JuvaBYNTue25350.doc', '2007-01-23', '', '', '14:53:50', 'admin', 1001, '2', '10002', '0', '', 'y');
INSERT INTO `detail_news` VALUES (33, 'กระทรวงวิทย์ฯ จัดประชุมเชิงปฏิบัติการเพิ่มขีดความสามารถ อุตฯ อาหารแช่แข็ง', '		 \r\n		  เมื่อวันจันทร์ที่ 28 พ.ค. 2550 ณ ห้องประชุม 102 สำนักงานพัฒนาวิทยาศาสตร์และเทคโนโลยีแห่งชาติกระทรวงวิทยาศาสตร์และเทคโนโลยี ร่วมกับ สมาคมนักวิชาชีพไทยในสหรัฐอเมริกาและแคนาดา (ATPAC) โดยการสนับสนุนจัดประชุมจากสำนักงานที่ปรึกษาด้านวิทยาศาสตร์และเทคโนโลยี 		  \r\n		  \r\n		  \r\n		  ', '<FONT size=2><FONT face=Tahoma>งานดังกล่าวได้รับเกียรติจาก ดร. อุไรวรรณ ตั้งประเสริฐชัย และ นายพิชัย&nbsp; <SPAN lang=TH>ตั้งประเสริฐชัย มาเป็นวิทยากรบรรยายเรื่อง</SPAN> ?<SPAN lang=TH>แนวโน้มผลิตภัณฑ์อาหารแช่แข็งในสหรัฐอเมริกา</SPAN>?&nbsp; ? <SPAN lang=TH>เทคโนโลยีอาหารอบแช่แข็ง</SPAN>?&nbsp; ?<SPAN lang=TH>การปรับปรุงคุณภาพผลิตภัณฑ์</SPAN>? <SPAN lang=TH>และ </SPAN>?<SPAN lang=TH>เทคโนโลยีกระบวนการผลิตและบรรจุภัณฑ์</SPAN>?&nbsp; <SPAN lang=TH>ซึ่งได้รับความสนใจจากผู้แทนหน่วยงานภาครัฐ และภาคอุตสาหกรรมอาหารจำนวนกว่า </SPAN>80 <SPAN lang=TH>คน เข้าร่วมประชุม เพื่อรับฟังข้อมูล และริเริ่มแนวคิดในการจัดทำกิจกรรมการวิจัยและพัฒนาเพื่อให้ได้เทคโนโลยีที่เหมาะสมแก่อุตสาหกรรมอาหารในประเทศไทยใน โดยวิทยากรมุ่งเน้นการถ่ายทอดความรู้เกี่ยวกับความก้าวหน้าเทคโนโลยีด้าน </SPAN>Frozen Baking&nbsp; <SPAN lang=TH>เพื่อการพัฒนาสินค้าอาหารกึ่งสำเร็จรูปหรือสำเร็จรูปที่แช่เยือกแข็งจากสหรัฐอเมริกาให้แก่ผู้ประกอบการผลิตเพื่อบริโภคและส่งออกสินค้าอาหาร ได้นำไปใช้เพิ่มขีดความสามารถทั้งในกระบวนการผลิต</SPAN>&nbsp; <SPAN lang=TH>การวิจัยและพัฒนา การพัฒนาคุณภาพและความปลอดภัยของอาหาร</SPAN> <BR>&nbsp;<SPAN lang=TH><SPAN style="mso-tab-count: 1">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </SPAN>ทั้งนี้ กระทรวงวิทยาศาสตร์ จะได้นำประเด็นปัญหาที่ประสบในการผลิตที่ได้รับทราบจากภาคอุตสาหกรรมมารวบรวมเป็นหัวข้อการวิจัยในการต่อยอดการวิจัยและพัฒนาในประเด็นต่างๆ ที่เกี่ยวข้องต่อไป</SPAN></FONT></FONT><?xml:namespace prefix = o ns = "urn:schemas-microsoft-com:office:office" /><o:p></o:p> ', 'ไม่มีข้อมูลลง', 'TEboh0HDSC_8514.jpg', '2007-06-05', '2007-06-05', '2007-06-05', '10:51:22', 'admin', 1001, '2', '10002', '0', '', 'y');
INSERT INTO `detail_news` VALUES (40, '?กระทรวงวิทย์เปิดโรงงานปุ๋ยอินทรีย์คุณภาพสูง จ.ฉะเชิงเทรา สร้างความเข้มแข็งเศรษฐกิจระดับชุมชน', '		 \r\n		  เมื่อวันที่ 4 มิถุนายน 2550 ดร.ศักดิ์สิทธิ์ ตรีเดช ปลัดกระทรวงวิทยาศาสตร์และเทคโนโลยี  เป็นประธานในพิธีเปิดโรงงานต้นแบบผลิตปุ๋ยอินทรีย์คุณภาพสูงของกลุ่มเกษตรกรทำนาดอนฉิมพลี        อ.บางน้ำเปรี้ยว จ.ฉะเชิงเทรา  		 \r\n		  \r\n		  \r\n		  \r\n		  \r\n		  		  \r\n		  \r\n		  \r\n		  ', '<FONT size=2>ซึ่งการก่อสร้างโรงงานดังกล่าวอยู่ภายใต้โครงการการถ่ายทอดเทคโนโลยีโรงงานต้นแบบผลิตปุ๋ยอินทรีย์ ปุ๋ยอินทรีย์เคมีและปุ๋ยชีวภาพ เพื่อกระตุ้นเศรษฐกิจระดับชุมชนตามมติของคณะรัฐมนตรี โดยการดำเนินงานของสถาบันวิจัยวิทยาศาสตร์และเทคโนโลยีแห่งประเทศไทย (วว.) หน่วยงานรัฐวิสาหกิจในสังกัดของกระทรวงวิทยาศาสตร์และเทคโนโลยี&nbsp;&nbsp;<BR>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; ? ...กระทรวงวิทยาศาสตร์ฯ ตระหนักถึงความสำคัญของการรณรงค์ให้พี่น้องเกษตรกรหันมาใช้ปุ๋ยอินทรีย์ทดแทนการใช้ปุ๋ยเคมี จึงได้มอบหมายให้สถาบันวิจัยวิทยาศาสตร์ฯ ทำการวิจัยพัฒนากระบวนการผลิตปุ่ยอินทรีย์จากมูลสัตว์ จนสามารถถ่ายทอดเทคโนโลยีให้แก่เกษตรกรทั่วประเทศ ซึ่งโรงงานผลิตปุ๋ยอินทรีย์คุณภาพสูงแห่งนี้ คาดว่าจะเป็นประโยชน์อย่างยิ่งต่อเกษตรกรในพื้นที่ อ.บางน้ำเปรี้ยว และพื้นที่ใกล้เคียง&nbsp; ซึ่งจะได้ใช้ปุ๋ยคุณภาพ ที่ผลิตจากเกษตรกรเป็นผู้ผลิตเอง โดยยึดหลักวิชาการที่ถูกต้องในการผลิตและการใช้ปุ๋ย ส่งผลให้เกษตรกรมีรายได้เพิ่มขึ้น ทั้งยังเป็นแนวทางพึ่งพาตนเองตามนโยบายเศรษฐกิจพอเพียงของรัฐบาลอีกด้วย....? ปลัดกระทรวงวิทยาศาสตร์และเทคโนโลยี กล่าว<BR>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; ดร.นงลักษณ์ ปานเกิดดี ผู้ว่าการ วว. ชี้แจงเพิ่มเติมว่า กระทรวงวิทยาศาสตร์ฯ ได้มอบหมายให้ วว. ทำการวิจัยและพัฒนาเทคโนโลยีการผลิตปุ๋ยมาเป็นลำดับ จนกระทั่งประสบความสำเร็จสามารถถ่ายทอดเทคโนโลยีสู่กลุ่มเกษตรกรในท้องถิ่น และก่อสร้างโรงงานต้นแบบผลิตปุ๋ยอินทรีย์คุณภาพสูงมาตั้งแต่ปี 2546 - ปัจจุบัน เป็นจำนวน 170 โรง กระจายอยู่ทั่วทุกภูมิภาคของประเทศ สำหรับโรงงานต้นแบบผลิตปุ๋ยอินทรีย์คุณภาพสูงของกลุ่มเกษตรกรทำนาดอนฉิมพลี มีกำลังผลิตปุ๋ยอินทรีย์อัดเม็ดปีละ 200 ตัน สามารถครอบคลุมพื้นที่ทางการเกษตรประมาณ 4,000 ไร่ โดยโรงงานดังกล่าวประกอบด้วยอาคารขนาด&nbsp; 6 &#61620; 12&nbsp; เมตร จำนวน 2&nbsp; หลัง&nbsp; และมีเครื่องจักรและอุปกรณ์ที่ใช้ในกระบวนการผลิต ได้แก่ เครื่องอัดเม็ดปุ๋ย&nbsp; เครื่องตีป่น&nbsp; เครื่องผสมแบบแนวนอน เครื่องกลับปุ๋ย&nbsp; เครื่องเย็บกระสอบปุ๋ยและเครื่องชั่งปุ๋ย<BR>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; ดร.สุริยา&nbsp; สาสนรักกิจ&nbsp; ผู้อำนวยการศูนย์เทคโนโลยีปุ๋ย วว. กล่าวเพิ่มเติมว่า&nbsp;&nbsp; การผลิตปุ๋ยอินทรีย์คุณภาพสูงนั้น จะใช้วัสดุเหลือใช้/เหลือทิ้งทางการเกษตรเป็นวัตถุดิบ&nbsp; ได้แก่&nbsp;&nbsp; มูลสัตว์&nbsp;&nbsp; แกลบ&nbsp; กากตะกอนน้ำตาล&nbsp; กากมันสำปะหลัง เป็นต้น ทั้งนี้ ผลจากการถ่ายทอดเทคโนโลยีการผลิตปุ๋ยอินทรีย์คุณภาพสูงดังกล่าว วว.ได้รับการตอบรับและความพึงพอใจจากเกษตรกรเป็นอย่างดี เนื่องจากเป็นปุ๋ยที่บำรุงทั้งต้นไม้และบำรุงดิน ทำให้ต้นข้าว ไม้ผล เช่น ทุเรียน&nbsp; ขนุน&nbsp; ลองกอง&nbsp; ลำไย&nbsp; สัมโอ เจริญงอกงามดี ให้ผลผลิตสูงเป็นที่ต้องการของตลาด&nbsp; หากกลุ่มเกษตรกรหรือผู้สนใจทั่วไป&nbsp;<BR>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; ขอรับคำแนะนำปรึกษาด้านการใช้ปุ๋ยอย่างครบวงจร</FONT> ', 'ไม่มีข้อมูลลง', 'ไม่มีข้อมูลลง', '2007-06-06', '2007-06-06', '2007-06-06', '10:45:17', 'admin', 1001, '2', '10002', '0', '', 'y');
INSERT INTO `detail_news` VALUES (42, 'สุขทุกข์ในมือเรา', ' ชีวิตไม่อาจเป็นไปดังหวังได้หมด \r\nเปรียบไปก็ไม่ต่างกับเกมกีฬาที่ต้องมีแพ้ชนะ ใครที่หวังชัยชนะไปเสียทุกครั้ง ก็เตรียมตัวทุกข์ใจไว้ได้เลย		 \r\n		  \r\n		  \r\n		  \r\n		  \r\n		  		  \r\n		  \r\n		  \r\n		  ', '<DIV><STRONG><FONT color=#000080>พูดเช่นนี้มิได้หมายความว่า<BR><BR></FONT></STRONG>ให้เตรียมตัวเป็นผู้แพ้แต่อยู่ในมุ้งหามิได้ ใครที่คิดเช่นนั้น ก็คงงอมืองอเท้าตั้งแต่แรก คนเราควรพากเพียรอย่างเต็มที่ แต่ใครเล่าที่จะกำหนดผลสำเร็จไปได้หมด คนที่มีน้ำใจเป็นนักกีฬา ต้องรู้จักแพ้ให้เป็น ฉันใด ในยามที่ชีวิตตกต่ำ บัณฑิตพึงรู้จักทำใจให้เป็นฉันนั้น <BR><BR><B><FONT color=navy>แต่คนเรามักทำใจไม่ได้ </FONT></B><BR><BR>เพราะคอยหวนคะนึงถึงวันคืนอันชื่นบาน <B><FONT color=red>สมัยยังมั่งมีศรีสุข หรือยังมีหน้ามีตา </FONT></B>แต่อดีตนั้นมีไว้เพื่อเป็นฐานหนุนส่งให้เกิดปัจจุบัน เพื่อก้าวไปสู่อนาคต ใครที่เอาอดีตมาเหนี่ยวรั้งตนไว้ไม่ให้ไปข้างหน้า ก็เท่ากับเป็นนักโทษในกรงขังของตัวเอง </DIV>\r\n<DIV><STRONG><FONT color=#003399>ชีวิตนั้นให้รางวัลก็แต่ผู้ที่อยู่กับปัจจุบัน<BR><BR></FONT></STRONG>เพราะปัจจุบันเท่านั้นที่เป็นของจริง จริงอยู่บ่อยครั้งปัจจุบันมีแต่เรื่องระทมทุกข์ อดีตนั้นน่าพิสมัยมากกว่า <B><FONT color=red>แต่ถ้าเราจะหวนไปหาอดีตบ้างก็ควรเป็นชั่วครู่ชั่วขณะ</FONT></B> เพื่อชุบชูจิตใจให้ชื่นบานจะได้มีกำลังมาฟันฝ่าชีวิตในปัจจุบัน <BR><BR><B><FONT color=navy>อย่างไรก็ตาม </FONT></B><BR><BR>อย่าลืมว่าชีวิตจะสุขหรือทุกข์นั้น อยู่ที่เราเป็นสำคัญ ไม่ใช่เพราะคนอื่นหรือเหตุอื่น เช่นเดียวกับความจนและความรวยนั้น อยู่ที่มุมมองของเรา คนไทยเป็นอันมาก<B><FONT color=red> กำลังทุกข์เพราะคิดอยู่ตลอดเวลาว่าตัวเองยากจนลง</FONT></B> ตราบใดที่ยังจมอยู่กับความคิดแบบนี้ก็ไม่มีวันคลายทุกข์ไปได้ <BR><BR><B><FONT color=navy>แต่เราลืมไปแล้วหรือว่า </FONT></B><BR><BR>ชีวิตความเป็นอยู่ของเราตอนนี้ ถึงอย่างไรก็ยังดีกว่าบางช่วงในอดีต ลองมองไปรอบตัว จะพบว่า<B><FONT color=red> ข้าวของในบ้านหลายชิ้น เคยเป็นของสุดเอื้อมสมัยเรายังเด็ก หรือเป็นหนุ่มเป็นสาว รถยนต์ แก้วแหวนเงินทอง วีดีโอ สเตอริโอ ซีดี ฯลฯ </FONT></B><BR><BR><B><FONT color=navy>มองในแง่นี้เราไม่ได้จนลงแต่รวยขึ้นต่างหาก </FONT></B><BR><BR>ยิ่งถ้าเปรียบกับคนอีกมากมายตอนนี้ ไม่ว่าจะมองลงไปข้างล่างหรือมองขึ้นข้างบน เราก็ยังสบายกว่ามาก คนที่ถูกยึดรถไป สมควรแล้วที่จะคิดว่า <B><FONT color=red>ตัวเองโชคดีกว่าคนที่กิจการล้มละลาย</FONT></B> ส่วนคนที่กิจการล้มละลาย ก็ยังนับว่าโชคดีกว่านักธุรกิจพันล้าน ที่มีหนี้ท่วมหัว <BR><STRONG><FONT color=#003399>แต่ถึงจะเป็นหนี้เป็นสินมากแค่ไหน <BR><BR></FONT></STRONG>เราก็ยังมีสิทธิเลือกได้ว่าตัวเองจะทุกข์หรือไม่ทุกข์ เพราะถึงที่สุดสุขหรือทุกข์ไม่ได้อยู่ที่จำนวนทรัพย์หรือหนี้สิน หากอยู่ที่ใจของเรา ถ้าเราคิดอยู่ตลอดเวลาว่าตัว<B><FONT color=red> แย่แล้ว ๆ นรกก็เกิดขึ้นทันตาเห็น เรามีสิทธิที่จะคิดว่าตัวเองไม่แย่</FONT></B> แต่สิทธินี้เราจะใช้หรือไม่อยู่ที่ใคร ถ้าไม่ใช่ตัวเราเอง <BR><BR><B><FONT color=navy>กระนั้นก็ตาม </FONT></B><BR><BR>นอกจากวิธีคิดหรือมุมมองแล้ว คุณภาพจิตของเราก็สำคัญ ถ้าเราเป็นคนรู้จักสันโดษ ยินดีในสิ่งที่ตัวเองได้มาหรือมีอยู่ มีน้ำใจเอื้อเฟื้อเผื่อแผ่ รู้จักแสวงหาความสงบในจิตใจ วิกฤตเศรษฐกิจที่กำลังเผชิญอยู่ขณะนี้ ก็ไม่ใช่เรื่องเหลือวิสัยที่จะฟันฝ่าไปได้.</DIV>', 'ไม่มีข้อมูลลง', 'ไม่มีข้อมูลลง', '2007-06-08', '2007-06-08', '2007-06-08', '15:43:38', 'admin', 1005, '2', '10002', '0', '', 'y');
INSERT INTO `detail_news` VALUES (44, '?ศูนย์ซินโครตรอนแห่งชาติ จับมือ ศูนย์นาโนเทคโนโลยีแห่งชาติ ร่วมใช้ประโยชน์แสงซินโครตรอนเพื่อพัฒนางาน', '		 \r\n		  เมื่อวันที่ 5 มิถุนายน 2550 ณ สำนักงานปลัดกระทรวงวิทยาศาสตร์เทคโนโลยี กรุงเทพมหานคร ดร.ศักดิ์สิทธิ์ ตรีเดช ปลัดกระทรวงวิทยาศาสตร์และเทคโนโลยี และประธานคณะกรรมการบริหารศูนย์ปฏิบัติการวิจัยเครื่องกำเนิดแสงซินโครตรอนแห่งชาติ 		 \r\n		  \r\n		  \r\n		  \r\n		  \r\n		  		  \r\n		  \r\n		  \r\n		  ', '<FONT size=2>ร่วมลงนามในบันทึกความร่วมมือทางวิชาการ กับ ศาสตรจารย์ ดร.ไพรัช ธัชยพงษ์ ประธานคณะกรรมการบริหารศูนย์นาโนเทคโนโลยีแห่งชาติ สำนักงานพัฒนาวิทยาศาสตร์และเทคโนโลยีแห่งชาติ (สวทช.) โดยมี รองศาสตราจารย์วีระพงษ์ แพสุวรรณ ผู้อำนวยการศูนย์ปฏิบัติการวิจัยเครื่องกำเนิดแสงซินโครตรอนแห่งชาติ และ ศาสตราจารย์ ดร.วิวัฒน์ ตัณฑะพานิชกุล ผู้อำนวยการศูนย์นาโนเทคโนโลยีแหงชาติ ร่วมเป็นสักขีพยาน ในการจับมือทางวิชาการครั้งนี้เพื่อร่วมกันพัฒนางานวิจัยโดยเฉพาะอย่างยิ่งด้านนาโนเทคโนโลยี การร่วมใช้เครื่องมือทางวิทยาศาสตร์ระหว่างห้องปฏิบัติการวิจัย การร่วมกันจัดประชุมวิชาการ การพัฒนาและแลกเปลี่ยนบุคลากรระหว่างสถาบัน และร่วมใช้ประโยชน์จากแสงซินโครตรอนเพื่อเป็นส่วนหนึ่งในการพัฒนางานวิจัยในระดับไมโคร-นาโนของประเทศ มุ่งไปสู่การยกระดับคุณภาพชีวิตของประชาชน เพิ่มขีดความสามารถของภาคอุตสาหกรรมไทยที่แข่งขันได้ในเวทีโลก<BR>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;ห้องปฏิบัติการแสงสยาม ได้ให้บริการแสงซินโครตรอน ผ่านสถานีทดลองระบบลำเลียงแสงซินโครตรอน จำนวน 3 สถานี ในย่านพลังงานตั้งแต่ระดับอัลตราไวโอเลต ถึงย่านเอกซเรย์พลังงานต่ำ&nbsp; (soft x-ray) ครอบคลุมงานวิจัยทางด้านฟิสิกส์พื้นฐาน ฟิสิกส์ประยุกต์ทางวิทยาศาสตร์สิ่งแวดล้อมการเกษตร การแพทย์ และอุตสาหกรรม และในอนาคตจะเพิ่มการให้บริการแสงซินโครตรอนในย่านเอกซเรย์พลังานสูง (hard x-ray) ห้องปฏิบัติการแสงสยามมีสมาชิกกลุ่มผู้ใช้บริการแสงซินโครตรอนซึ่งประกอบด้วยนักวิจัยจากสถานอุดมศึกษา หน่วยงานวิจัยทั้งภาครัฐและเอกชนทั่วประเทศกว่า 700 คน มีโครงการวิจัยที่ขอเข้าใช้บริการแสงซินโครตรอนกว่า 100 โครงการ และมีเพิ่มมากขึ้นทุกปี อันจะก่อให้เกิดผลงานวิชาการในหลากหลายสาขาซึ่งเป็นที่ยอมรับในวงการวิจัยระดับนานาชาติ</FONT><BR>', 'ไม่มีข้อมูลลง', 'ไม่มีข้อมูลลง', '2007-06-08', '2007-06-08', '2007-06-08', '15:59:47', 'admin', 1001, '2', '10002', '0', '', 'y');
INSERT INTO `detail_news` VALUES (45, 'กระทรวงวิทย์ฯ ร่วมมือมูลนิธิธนาคารกรุงเทพ มอบรางวัลสิ่งประดิษฐ์ ปี ?49', 'นายโฆสิต  ปั้นเปี่ยมรัษฎ์  รองนายกรัฐมนตรี  ให้เกียรติเป็นประธานพิธีมอบรางวัลผู้ชนะการประกวดสิ่งประดิษฐ์คิดค้นทางวิทยาศาสตร์และเทคโนโลยี  ประจำปี 2549 ในหัวข้อ ?เครื่องจักรกลการ เกษตร พลังงาน และสิ่งแวดล้อมการเกษตร? ณ ห้องสีฟ', '<FONT face=Tahoma size=2>&nbsp;ดร. ศักดิ์สิทธิ์<SPAN style="mso-spacerun: yes">&nbsp; </SPAN>ตรีเดช<SPAN style="mso-spacerun: yes">&nbsp; </SPAN>ปลัดกระทรวงวิทยาศาสตร์และเทคโนโลยี<SPAN style="mso-spacerun: yes">&nbsp; </SPAN>เปิดเผยว่า<SPAN style="mso-spacerun: yes">&nbsp; </SPAN>กระทรวง วิทยาศาสตร์และเทคโนโลยี<SPAN style="mso-spacerun: yes">&nbsp; </SPAN>ร่วมกับมูลนิธิธนาคารกรุงเทพ<SPAN style="mso-spacerun: yes">&nbsp; </SPAN>เล็งเห็นความสำคัญของผู้มีความสามารถในการประดิษฐ์คิดค้นทางวิทยาศาสตร์และเทคโนโลยี<SPAN style="mso-spacerun: yes">&nbsp; </SPAN>ที่มีคุณค่าและเป็นประโยชน์ต่อประเทศชาติโดยรวม<SPAN style="mso-spacerun: yes">&nbsp; </SPAN>สมควรได้รับรางวัลตอบแทน<SPAN style="mso-spacerun: yes">&nbsp; </SPAN>เพื่อเป็นขวัญและกำลังใจให้แก่ผู้กระทำความดี<SPAN style="mso-spacerun: yes">&nbsp; </SPAN>จึงได้จัดให้มี<B>โครงการประกวดสิ่งประดิษฐ์คิดค้นทางวิทยาศาสตร์และเทคโนโลยีขึ้น</B><SPAN style="mso-spacerun: yes">&nbsp; </SPAN>ตั้งแต่ปี พ.ศ. 2536<SPAN style="mso-spacerun: yes">&nbsp; </SPAN>โดยเน้นเครื่องมือ<SPAN style="mso-spacerun: yes">&nbsp; </SPAN>เครื่องจักรกล<SPAN style="mso-spacerun: yes">&nbsp; </SPAN>หรือส่วนประกอบ/อุปกรณ์ด้านการเกษตรและสิ่งแวดล้อมการเกษตร<SPAN style="mso-spacerun: yes">&nbsp; </SPAN>โดยกระทรวงวิทยาศาสตร์ฯ<SPAN style="mso-spacerun: yes">&nbsp; </SPAN>เป็นแกนนำด้านวิชาการ<SPAN style="mso-spacerun: yes">&nbsp; </SPAN>การดำเนินการสรรหาพิจารณาคัดเลือก<SPAN style="mso-spacerun: yes">&nbsp; </SPAN>และตัดสินให้รางวัลสิ่งประดิษฐ์ฯ<SPAN style="mso-spacerun: yes">&nbsp; </SPAN>และมูลนิธิธนาคารกรุงเทพ<SPAN style="mso-spacerun: yes">&nbsp; </SPAN>เป็นหน่วยงานให้การสนับสนุนเงินรางวัล<SPAN style="mso-tab-count: 1">&nbsp;&nbsp;&nbsp; </SPAN><SPAN style="mso-tab-count: 1">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </SPAN><SPAN style="mso-spacerun: yes">&nbsp;&nbsp;</SPAN><BR><SPAN style="mso-spacerun: yes">&nbsp;</SPAN><SPAN style="mso-tab-count: 2">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </SPAN>ในปี พ.ศ. 2549<SPAN style="mso-spacerun: yes">&nbsp; </SPAN>ได้กำหนดหัวข้อ </FONT><SPAN style="FONT-SIZE: 10pt; FONT-FAMILY: Tahoma">?<SPAN lang=TH>เครื่องจักรกลการเกษตร<SPAN style="mso-spacerun: yes">&nbsp; </SPAN>พลังงาน<SPAN style="mso-spacerun: yes">&nbsp; </SPAN>และสิ่งแวดล้อมการเกษตร</SPAN>?<SPAN lang=TH> โดยได้เชิญชวนให้นักประดิษฐ์<SPAN style="mso-spacerun: yes">&nbsp; </SPAN>นักวิทยาศาสตร์<SPAN style="mso-spacerun: yes">&nbsp; </SPAN>นักเทคโนโลยี<SPAN style="mso-spacerun: yes">&nbsp; </SPAN>และประชาชนทั่วไป<SPAN style="mso-spacerun: yes">&nbsp; </SPAN>ส่งผลงานเข้าประกวด<SPAN style="mso-spacerun: yes">&nbsp; </SPAN>และคณะกรรมการฯ<SPAN style="mso-spacerun: yes">&nbsp; </SPAN>มีมติตัดสินให้รางวัลสิ่งประดิษฐ์คิดค้นทางวิทยาศาสตร์และเทคโนโลยี<SPAN style="mso-spacerun: yes">&nbsp; </SPAN>ประจำปี<SPAN style="mso-spacerun: yes">&nbsp; </SPAN>พ.ศ. 2549<SPAN style="mso-spacerun: yes">&nbsp; </SPAN>จำนวน<SPAN style="mso-spacerun: yes">&nbsp; </SPAN>5<SPAN style="mso-spacerun: yes">&nbsp; </SPAN>ผลงาน</SPAN><SPAN style="mso-spacerun: yes">&nbsp; </SPAN><SPAN lang=TH>ได้แก่<?xml:namespace prefix = o ns = "urn:schemas-microsoft-com:office:office" /><o:p></o:p></SPAN></SPAN>\r\n<P class=MsoNormal style="MARGIN: 0in 0in 0pt"><SPAN lang=TH style="FONT-SIZE: 10pt; FONT-FAMILY: Tahoma"><SPAN style="mso-spacerun: yes">&nbsp;</SPAN><SPAN style="mso-tab-count: 2">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </SPAN>รางวัลที่<SPAN style="mso-spacerun: yes">&nbsp; </SPAN>1<SPAN style="mso-spacerun: yes">&nbsp; </SPAN>ไม่มีผู้สมควรได้รับการคัดเลือก</SPAN><SPAN style="FONT-SIZE: 10pt; FONT-FAMILY: Tahoma"><o:p></o:p></SPAN></P>\r\n<P class=MsoNormal style="MARGIN: 0in 0in 0pt"><SPAN lang=TH style="FONT-SIZE: 10pt; FONT-FAMILY: Tahoma"><SPAN style="mso-spacerun: yes">&nbsp; </SPAN><SPAN style="mso-tab-count: 2">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </SPAN>รางวัลที่<SPAN style="mso-spacerun: yes">&nbsp; </SPAN>2<SPAN style="mso-spacerun: yes">&nbsp; </SPAN>ไม่มีผู้สมควรได้รับการคัดเลือก</SPAN><SPAN style="FONT-SIZE: 10pt; FONT-FAMILY: Tahoma"><o:p></o:p></SPAN></P>\r\n<P class=MsoNormal style="MARGIN: 0in 0in 0pt"><SPAN lang=TH style="FONT-SIZE: 10pt; FONT-FAMILY: Tahoma"><SPAN style="mso-tab-count: 2">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </SPAN>รางวัลที่<SPAN style="mso-spacerun: yes">&nbsp; </SPAN></SPAN><SPAN style="FONT-SIZE: 10pt; FONT-FAMILY: Tahoma">3<SPAN lang=TH><SPAN style="mso-spacerun: yes">&nbsp; </SPAN>รางวัลมูลค่า 100,000 บาท<SPAN style="mso-spacerun: yes">&nbsp; </SPAN>พร้อมโล่เชิดชูเกียรติ<SPAN style="mso-spacerun: yes">&nbsp; </SPAN>ได้แก่<SPAN style="mso-spacerun: yes">&nbsp; </SPAN></SPAN>?<SPAN lang=TH>เครื่องกระตุ้นการหลั่งน้ำเชื้อด้วยไฟฟ้า</SPAN>?<SPAN lang=TH><SPAN style="mso-spacerun: yes">&nbsp; </SPAN>ของนายธงชัย<SPAN style="mso-spacerun: yes">&nbsp; </SPAN>เสือน้อย<SPAN style="mso-spacerun: yes">&nbsp; </SPAN></SPAN><o:p></o:p></SPAN></P>\r\n<P class=MsoNormal style="MARGIN: 0in 0in 0pt"><SPAN lang=TH style="FONT-SIZE: 10pt; FONT-FAMILY: Tahoma"><SPAN style="mso-spacerun: yes">&nbsp;</SPAN><SPAN style="mso-tab-count: 2">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </SPAN>รางวัลชมเชย<SPAN style="mso-spacerun: yes">&nbsp; </SPAN>รางวัลมูลค่า<SPAN style="mso-spacerun: yes">&nbsp; </SPAN>50,000<SPAN style="mso-spacerun: yes">&nbsp; </SPAN>บาท<SPAN style="mso-spacerun: yes">&nbsp; </SPAN>พร้อมโล่เชิดชูเกียรติ ให้<SPAN style="mso-spacerun: yes">&nbsp; </SPAN>4 รางวัล<SPAN style="mso-spacerun: yes">&nbsp; </SPAN>ได้แก่</SPAN><SPAN style="FONT-SIZE: 10pt; FONT-FAMILY: Tahoma"><o:p></o:p></SPAN></P>\r\n<P class=MsoNormal style="MARGIN: 0in 0in 0pt"><SPAN lang=TH style="FONT-SIZE: 10pt; FONT-FAMILY: Tahoma"><SPAN style="mso-spacerun: yes">&nbsp;</SPAN><SPAN style="mso-tab-count: 2">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </SPAN>1.<SPAN style="mso-spacerun: yes">&nbsp; </SPAN></SPAN><SPAN style="FONT-SIZE: 10pt; FONT-FAMILY: Tahoma">?<SPAN lang=TH>เครื่องตัดหญ้าแบบวางราย<SPAN style="mso-spacerun: yes">&nbsp; </SPAN>ปฏิพงษ์ <?xml:namespace prefix = st1 ns = "urn:schemas-microsoft-com:office:smarttags" /><st1:metricconverter w:st="on" ProductID="2005?">2005<SPAN lang=EN-US>?</SPAN></st1:metricconverter> <SPAN style="mso-tab-count: 1">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </SPAN>ของ<SPAN style="mso-spacerun: yes">&nbsp; </SPAN>นายสุเมธ<SPAN style="mso-spacerun: yes">&nbsp; </SPAN>อมรพิมล </SPAN><o:p></o:p></SPAN></P>\r\n<P class=MsoNormal style="MARGIN: 0in 0in 0pt"><SPAN lang=TH style="FONT-SIZE: 10pt; FONT-FAMILY: Tahoma"><SPAN style="mso-tab-count: 2">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </SPAN>2.<SPAN style="mso-spacerun: yes">&nbsp; </SPAN></SPAN><SPAN style="FONT-SIZE: 10pt; FONT-FAMILY: Tahoma">?<SPAN lang=TH>เครื่องจักกก</SPAN>?<SPAN lang=TH> <SPAN style="mso-tab-count: 4">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </SPAN>ของ<SPAN style="mso-spacerun: yes">&nbsp; </SPAN>นายปัญญา<SPAN style="mso-spacerun: yes">&nbsp; </SPAN>เทียนนาวา</SPAN><BR><SPAN lang=TH><SPAN style="mso-tab-count: 2">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </SPAN>3.<SPAN style="mso-spacerun: yes">&nbsp; </SPAN></SPAN>?<SPAN lang=TH>เครื่องอบแห้งเชิงพานิชย์โดยใช้คลื่นไมโครเวฟที่มีการป้อนคลื่นสองตำแหน่ง<BR><SPAN style="mso-tab-count: 2">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </SPAN><SPAN style="mso-spacerun: yes">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </SPAN>แบบไม่สมมาตรกับระบบสเปาเต็ดเบด (</SPAN>MW-Dry2<SPAN lang=TH>) ของ นายผดุงศักดิ์<SPAN style="mso-spacerun: yes">&nbsp; </SPAN>รัตนเดโช<SPAN style="mso-tab-count: 2">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </SPAN>4.<SPAN style="mso-spacerun: yes">&nbsp; </SPAN></SPAN>?<SPAN lang=TH>เครื่องตรวจความผิดปกติของนม </SPAN>?U.H.T. <SPAN lang=TH>บรรจุกล่อง แบบไม่ทำลาย</SPAN>?<SPAN lang=TH><SPAN style="mso-spacerun: yes">&nbsp; </SPAN><BR><SPAN style="mso-spacerun: yes">&nbsp;</SPAN><SPAN style="mso-tab-count: 8">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </SPAN>ของ<SPAN style="mso-spacerun: yes">&nbsp; </SPAN>นาย สุวรรณ<SPAN style="mso-spacerun: yes">&nbsp; </SPAN>หอมหวล</SPAN><o:p></o:p></SPAN></P>', 'ไม่มีข้อมูลลง', 'nW5MncTchangmai1.jpg', '2007-06-08', '2007-06-08', '2007-06-08', '16:02:27', 'admin', 1003, '1', '10002', '0', '', 'y');
INSERT INTO `detail_news` VALUES (38, 'กระทรวงวิทย์ฯ รณรงค์กิจกรรมปีสุริยฟิสิกส์สากล ผลิตแอนนิเมชั่น ปลูกฝังเยาวชนเข้าถึงความรู้ดาราศาสตร์', 'กระทรวงวิทย์ฯปลูกฝังเยาวชนเข้าถึงความรู้ด้านดาราศาสตร์รับปีสุริยฟิสิกส์สากล 2007 และ สัปดาห์อวกาศโลก จัดงานแถลงข่าวเปิดตัวภาพยนตร์การ์ตูนแอนนิเมชั่น เรื่อง ?ตำนานดวงอาทิตย์กับกระต่าย? และ ?โครงการประกวดนิทานดาว?		 \r\n		  \r\n		 ', '<SPAN lang=TH style="FONT-SIZE: 10pt; FONT-FAMILY: Tahoma">เมื่อวันพฤหัสบดีที่ 24 พ.ค. 2550 ณ ห้องโถงชั้น1 อาคารพระจอมเกล้า กระทรวงวิทยาศาสตร์และเทคโนโลยี โดยมี ดร. ศักดิ์สิทธิ์ ตรีเดช ปลัดกระทรวงวิทยาศาสตร์และเทคโนโลยี เป็นประธานการแถลงข่าว และ รศ. บุญรักษา สุนทรธรรม ผู้อำนวยการสถาบันวิจัยดาราศาสตร์แห่งชาติ และนาย<?xml:namespace prefix = st1 ns = "urn:schemas-microsoft-com:office:smarttags" /><st1:PersonName w:st="on" ProductID="สุรชัย รัตนเสริมพงศ์">สุรชัย รัตนเสริมพงศ์</st1:PersonName> รองผู้อำนวยการสำนักงานพัฒนาเทคโนโลยีอวกาศและภูมิสารสนเทศ ร่วมแถลงข่าว</SPAN><SPAN style="FONT-SIZE: 10pt; FONT-FAMILY: Tahoma"><?xml:namespace prefix = o ns = "urn:schemas-microsoft-com:office:office" /><o:p></o:p></SPAN>\r\n<P class=MsoNormal style="TEXT-JUSTIFY: inter-cluster; MARGIN: 0in 0in 0pt; TEXT-INDENT: 0.5in; TEXT-ALIGN: justify"><SPAN style="FONT-SIZE: 10pt; FONT-FAMILY: Tahoma"><o:p>&nbsp;</o:p></SPAN></P>\r\n<P class=MsoNormal style="MARGIN: 0in 0in 0pt; TEXT-INDENT: 0.5in; TEXT-ALIGN: justify"><SPAN lang=TH style="FONT-SIZE: 10pt; FONT-FAMILY: Tahoma"><SPAN style="mso-tab-count: 1">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </SPAN><B>ดร. ศักดิ์สิทธิ์ ตรีเดช ปลัดกระทรวงวิทยาศาสตร์และเทคโนโลยี</B> เปิดเผยว่า </SPAN><SPAN style="FONT-SIZE: 10pt; FONT-FAMILY: Tahoma">?<SPAN lang=TH>เนื่องด้วย ปี พ.ศ. </SPAN>2550 (<SPAN lang=TH>ค.ศ. </SPAN>2007) <SPAN lang=TH>เป็นปีที่สหพันธ์ธรณีฟิสิกส์แห่งอเมริกาได้กำหนดให้มีการฉลองครบรอบ </SPAN>50 <SPAN lang=TH>ปีแห่งปีธรณีฟิสิกส์สากล </SPAN>(International Geophysical Year, IGY) <SPAN lang=TH>และครบรอบ </SPAN>50 <SPAN lang=TH>ปีแห่งการสำรวจอวกาศ โดยในปี พ.ศ. </SPAN>2550 <SPAN lang=TH>นี้เป็นปีที่จะริเริ่มกิจกรรมปีสุริยฟิสิกส์สากล </SPAN>(International Heliophysical Year, 2007 (IHY 2007)<SPAN lang=TH> ทั้งนี้ ตามนโยบายของรัฐบาล และกระทรวงวิทยาศาสตร์และเทคโนโลยีได้ให้ความสำคัญเรื่องการสร้างปัญญาโดยปลูกฝังวิทยาศาสตร์และเทคโนโลยีให้เข้าถึงประชาชนในระดับต่างๆ ซึ่งนโยบายเร่งด่วนประการหนึ่งของกระทรวงวิทยาศาสตร์ฯ ได้แก่ โครงการ </SPAN><B>?<SPAN lang=TH>ปลูกฝังปัญญาเยาว์</SPAN>?</B><SPAN lang=TH> โดยสนับสนุนนันทนาการของเด็กก่อนวัยเรียนด้วยการกระตุ้นและหาวิธีการให้เด็กก่อนวัยเรียนสนใจและอยากที่จะเรียนรู้เนื้อหาสาระทางวิทยาการที่เหมาะสมกับวัย</SPAN>?<SPAN lang=TH> </SPAN><o:p></o:p></SPAN></P>\r\n<P class=MsoNormal style="MARGIN: 0in 0in 0pt; TEXT-INDENT: 0.5in; TEXT-ALIGN: justify"><SPAN lang=TH style="FONT-SIZE: 10pt; FONT-FAMILY: Tahoma"><SPAN style="mso-tab-count: 1">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </SPAN><SPAN style="mso-spacerun: yes">&nbsp;</SPAN><B>สถาบันวิจัยดาราศาสตร์แห่งชาติ (สดร.)</B> เป็นหน่วยงานหลักของประเทศไทย ในการรณรงค์ประชาสัมพันธ์ ร่วมกับ <B>สำนักพัฒนาเทคโนโลยีอวกาศและภูมิสารสนเทศ (องค์การมหาชน) หรือ<SPAN style="mso-spacerun: yes">&nbsp; </SPAN>สทอภ.</B> ดังนั้น จึงได้ร่วมกันจัดงานแถลงข่าวเกี่ยวกับปีสุริยฟิสิกส์สากล 2007 เพื่อเป็นการประชาสัมพันธ์กิจกรรมต่าง ๆ ที่จะจัดขึ้นในปีนี้ ได้แก่ <B>การ์ตูนแอนนิเมชั่น เรื่อง ตำนานดวงอาทิตย์กับกระต่าย</B> และ <B>โครงการประกวดนิทานดาว</B> รวมทั้งกิจกรรมการให้ความรู้ทางดาราศาสตร์แก่ผู้ปกครองและผู้ที่เกี่ยวข้องกับการดูแลเด็กก่อนวัยเรียน ซึ่งเหมาะสมอย่างยิ่งที่จะนำไปเป็นสื่อการเรียนการสอนในเรื่องดาราศาสตร์สำหรับโรงเรียนและหน่วยงานที่สนใจ เพื่อเป็นการปลูกฝังปัญญาเยาว์ให้กับเยาวชนในเรื่องของดาราศาสตร์</SPAN><SPAN style="FONT-SIZE: 10pt; FONT-FAMILY: Tahoma"><o:p></o:p></SPAN></P>\r\n<P class=MsoNormal style="MARGIN: 0in 0in 0pt"><SPAN lang=TH style="FONT-SIZE: 10pt; FONT-FAMILY: Tahoma"><SPAN style="mso-tab-count: 2">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </SPAN>งานแถลงข่าวดังกล่าวได้รับความสนใจจากนักเรียนชั้นอนุบาล ชั้นประถมและระดับมัธยมมาร่วมงานด้วยความตื่นเต้น โดยผู้จัดได้นำมาสค็อตน่ารัก ๆ จากการ์ตูนแอนนิเมชั่น เรื่องตำนานดวงอาทิตย์กับกระต่าย และ<SPAN style="mso-spacerun: yes">&nbsp;&nbsp;&nbsp;&nbsp; </SPAN>พี่ธีออส กับ พี่</SPAN><SPAN style="FONT-SIZE: 10pt; FONT-FAMILY: Tahoma"> Gistda <SPAN lang=TH>มาร่วมสร้างสีสันภายในงานพร้อมทั้ง เล่นเกมส์ ตอบคำถามรับของรางวัล เป็นต้น</SPAN><o:p></o:p></SPAN></P>\r\n<P class=MsoNormal style="TEXT-JUSTIFY: inter-cluster; MARGIN: 0in 0in 0pt; TEXT-INDENT: 0.5in; TEXT-ALIGN: justify"><SPAN lang=TH style="FONT-SIZE: 10pt; FONT-FAMILY: Tahoma"><SPAN style="mso-tab-count: 1">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </SPAN>สำหรับ โครงการการประกวดนิทานดาว มีวัตถุประสงค์เพื่อกระตุ้นให้เยาวชนและผู้สนใจได้ร่วมส่ง<SPAN style="mso-spacerun: yes">&nbsp; </SPAN>ผลงาน นิทานเกี่ยวกับดวงจันทร์ โดยมีเนื้อเรื่องสอดแทรกความรู้เกี่ยวกับปรากฏการณ์ต่างๆ ของดวงจันทร์ ได้แก่<SPAN style="mso-spacerun: yes">&nbsp;&nbsp;&nbsp; </SPAN>การเกิดข้างขึ้น ข้างแรม ดวงจันทร์เต็มดวง เดือนมืด และปรากฏการณ์จันทรุปราคา<SPAN style="mso-spacerun: yes">&nbsp; </SPAN>หลังจากผลการตัดสินเป็นที่สิ้นสุดแล้ว<SPAN style="mso-spacerun: yes">&nbsp; </SPAN>ผลงานของผู้ได้รับรางวัลชนะเลิศ ทางสถาบันฯ จะนำไปทำเป็นการ์ตูนแอนนิเมชั่นเพื่อเผยแพร่ต่อไป ทั้งนี้ ผู้สนใจสามารถติดตามรายละเอียดเพิ่มเติมได้ที่ </SPAN><SPAN style="FONT-SIZE: 10pt; FONT-FAMILY: Tahoma"><A href="http://www.narit.or.th/">www.narit.or.th</A><SPAN style="mso-spacerun: yes">&nbsp; </SPAN><o:p></o:p></SPAN></P>', 'ไม่มีข้อมูลลง', 'xK70dkbchangmai1.jpg', '2007-06-05', '2007-06-05', '2007-06-05', '11:43:39', 'admin', 1003, '1', '10002', '0', '', 'y');
